  async createForumComment(comment: InsertForumComment): Promise<ForumComment> {
    return await db.transaction(async (tx) => {
      const [newComment] = await tx.insert(forumComments).values(comment).returning();

      await tx
        .update(forumThreads)
        .set({ 
          commentsCount: sql`${forumThreads.commentsCount} + 1`,
          updatedAt: new Date()
        })
        .where(eq(forumThreads.id, comment.threadId));

      return newComment;
    });
  }

  async getForumComments(threadId: string, limit: number = 50): Promise<ForumComment[]> {
    return await db
      .select()
      .from(forumComments)
      .where(eq(forumComments.threadId, threadId))
      .orderBy(desc(forumComments.createdAt))
      .limit(limit);
  }

  async voteForumThread(threadId: string, userId: string, vote: 'up' | 'down'): Promise<void> {
    await db.transaction(async (tx) => {
      const thread = await tx
        .select()
        .from(forumThreads)
        .where(eq(forumThreads.id, threadId))
        .for('update')
        .limit(1);

      if (!thread[0]) {
        throw new NotFoundError(`Forum thread ${threadId} not found`);
      }

      // Check if user has already voted
      const existingVote = await tx
        .select()
        .from(forumThreadVotes)
        .where(
          sql`${forumThreadVotes.threadId} = ${threadId} AND ${forumThreadVotes.userId} = ${userId}`
        )
        .limit(1);

      if (existingVote.length > 0) {
        const oldVote = existingVote[0].voteType;

        // If voting the same way, do nothing
        if (oldVote === vote) {
          return;
        }

        // User is changing their vote - remove old vote count and add new one
        if (oldVote === 'up' && vote === 'down') {
          await tx
            .update(forumThreads)
            .set({ 
              upvotes: sql`${forumThreads.upvotes} - 1`,
              downvotes: sql`${forumThreads.downvotes} + 1`
            })
            .where(eq(forumThreads.id, threadId));
        } else if (oldVote === 'down' && vote === 'up') {
          await tx
            .update(forumThreads)
            .set({ 
              downvotes: sql`${forumThreads.downvotes} - 1`,
              upvotes: sql`${forumThreads.upvotes} + 1`
            })
            .where(eq(forumThreads.id, threadId));
        }

        // Update the vote record
        await tx
          .update(forumThreadVotes)
          .set({ voteType: vote })
          .where(eq(forumThreadVotes.id, existingVote[0].id));
      } else {
        // First time voting - insert new vote record
        await tx.insert(forumThreadVotes).values({
          threadId,
          userId,
          voteType: vote,
        });

        // Update vote count
        if (vote === 'up') {
          await tx
            .update(forumThreads)
            .set({ upvotes: sql`${forumThreads.upvotes} + 1` })
            .where(eq(forumThreads.id, threadId));
        } else {
          await tx
            .update(forumThreads)
            .set({ downvotes: sql`${forumThreads.downvotes} + 1` })
            .where(eq(forumThreads.id, threadId));
        }
      }
    });
  }

  async voteForumComment(commentId: string, userId: string, vote: 'up' | 'down'): Promise<void> {
    await db.transaction(async (tx) => {
      const comment = await tx
        .select()
        .from(forumComments)
        .where(eq(forumComments.id, commentId))
        .for('update')
        .limit(1);

      if (!comment[0]) {
        throw new NotFoundError(`Forum comment ${commentId} not found`);
      }

      // Check if user has already voted
      const existingVote = await tx
        .select()
        .from(forumCommentVotes)
        .where(
          sql`${forumCommentVotes.commentId} = ${commentId} AND ${forumCommentVotes.userId} = ${userId}`
        )
        .limit(1);

      if (existingVote.length > 0) {
        const oldVote = existingVote[0].voteType;

        // If voting the same way, do nothing
        if (oldVote === vote) {
          return;
        }

        // User is changing their vote - remove old vote count and add new one
        if (oldVote === 'up' && vote === 'down') {
          await tx
            .update(forumComments)
            .set({ 
              upvotes: sql`${forumComments.upvotes} - 1`,
              downvotes: sql`${forumComments.downvotes} + 1`
            })
            .where(eq(forumComments.id, commentId));
        } else if (oldVote === 'down' && vote === 'up') {
          await tx
            .update(forumComments)
            .set({ 
              downvotes: sql`${forumComments.downvotes} - 1`,
              upvotes: sql`${forumComments.upvotes} + 1`
            })
            .where(eq(forumComments.id, commentId));
        }

        // Update the vote record
        await tx
          .update(forumCommentVotes)
          .set({ voteType: vote })
          .where(eq(forumCommentVotes.id, existingVote[0].id));
      } else {
        // First time voting - insert new vote record
        await tx.insert(forumCommentVotes).values({
          commentId,
          userId,
          voteType: vote,
        });

        // Update vote count
        if (vote === 'up') {
          await tx
            .update(forumComments)
            .set({ upvotes: sql`${forumComments.upvotes} + 1` })
            .where(eq(forumComments.id, commentId));
        } else {
          await tx
            .update(forumComments)
            .set({ downvotes: sql`${forumComments.downvotes} + 1` })
            .where(eq(forumComments.id, commentId));
        }
      }
    });
  }

  // Phase 5: Achievements
  async createAchievement(achievement: InsertAchievement): Promise<Achievement> {
    const result = await db.insert(achievements).values(achievement).returning();
    return result[0];
  }

  async getAchievements(): Promise<Achievement[]> {
    return await db.select().from(achievements);
  }

  async awardAchievement(userId: string, achievementId: string): Promise<UserAchievement> {
    const existing = await db
      .select()
      .from(userAchievements)
      .where(
        sql`${userAchievements.userId} = ${userId} AND ${userAchievements.achievementId} = ${achievementId}`
      )
      .limit(1);

    if (existing.length > 0) {
      return existing[0];
    }

    const result = await db.insert(userAchievements).values({ userId, achievementId }).returning();
    return result[0];
  }

  async getUserAchievements(userId: string): Promise<Array<UserAchievement & { achievement: Achievement }>> {
    const result = await db
      .select({
        userAchievement: userAchievements,
        achievement: achievements
      })
      .from(userAchievements)
      .innerJoin(achievements, eq(userAchievements.achievementId, achievements.id))
      .where(eq(userAchievements.userId, userId))
      .orderBy(desc(userAchievements.earnedAt));

    return result.map(row => ({
      ...row.userAchievement,
      achievement: row.achievement
    }));
  }

  async hasAchievement(userId: string, achievementId: string): Promise<boolean> {
    const result = await db
      .select()
      .from(userAchievements)
      .where(
        sql`${userAchievements.userId} = ${userId} AND ${userAchievements.achievementId} = ${achievementId}`
      )
      .limit(1);
    return result.length > 0;
  }

  // Phase 6: FAQs
  async createFaq(faq: InsertFaq): Promise<Faq> {
    const result = await db.insert(faqs).values(faq).returning();
    return result[0];
  }

  async getFaqs(category?: string): Promise<Faq[]> {
    let query = db.select().from(faqs);

    if (category) {
      query = query.where(eq(faqs.category, category)) as any;
    }

    return await query.orderBy(faqs.order, desc(faqs.createdAt));
  }

  async updateFaq(
    faqId: string,
    updates: Partial<Pick<Faq, 'question' | 'answer' | 'category' | 'order'>>
  ): Promise<Faq> {
    const result = await db
      .update(faqs)
      .set({ ...updates, updatedAt: new Date() })
      .where(eq(faqs.id, faqId))
      .returning();

    if (!result[0]) {
      throw new NotFoundError(`FAQ ${faqId} not found`);
    }
    return result[0];
  }

  async deleteFaq(faqId: string): Promise<void> {
    await db.delete(faqs).where(eq(faqs.id, faqId));
  }

  // AMM calculation methods
  private calculateAmmTrade(
    side: 'yes' | 'no',
    amount: number, // Amount user is spending (cash/points)
    yesPool: number, // Share inventory for YES
    noPool: number   // Share inventory for NO
  ): {
    shares: number; // Shares received by the user
    newYesPool: number;
    newNoPool: number;
    avgPrice: number // Average price per share for this trade
  } {
    // Single-pool CPMM for prediction markets
    // k = yesPool * noPool (constant product)
    // When buying YES: user deposits cash into NO pool, receives YES shares
    // When buying NO: user deposits cash into YES pool, receives NO shares
    // This ensures Price(YES) = noPool/(yesPool+noPool) and cost  shares  price

    const k = yesPool * noPool;

    if (side === 'yes') {
      // Buying YES shares
      // User deposits 'amount' (cash) into NO pool
      const newNoPool = noPool + amount;

      // Maintain constant product: yesPool * noPool = k
      // newYesPool * newNoPool = k
      // Avoid division by zero
      const newYesPool = newNoPool > 0 ? k / newNoPool : 0;

      // Shares given to user = reduction in YES share inventory
      const shares = yesPool - newYesPool;

      // Calculate average price (total cost / shares received)
      // Avoid division by zero if shares is 0
      const avgPrice = shares > 0 ? amount / shares : 0;

      return {
        shares,
        newYesPool,
        newNoPool,
        avgPrice
      };
    } else {
      // Buying NO shares
      // User deposits 'amount' (cash) into YES pool
      const newYesPool = yesPool + amount;

      // Maintain constant product
      const newNoPool = newYesPool > 0 ? k / newYesPool : 0;

      // Shares given to user = reduction in NO share inventory
      const shares = noPool - newNoPool;

      // Calculate average price
      const avgPrice = shares > 0 ? amount / shares : 0;

      return {
        shares,
        newYesPool,
        newNoPool,
        avgPrice
      };
    }
  }

  // Selling shares returns cash based on CPMM formula
  private calculatePayoutForSell(
    sharesToSell: number,
    side: 'yes' | 'no',
    yesPool: number, // Share inventory for YES
    noPool: number   // Share inventory for NO
  ): { payout: number; newYesPool: number; newNoPool: number } {
    // Single-pool CPMM for selling
    // User returns shares to pool, receives cash from opposite pool
    // Maintains constant product: k = yesPool * noPool

    const k = yesPool * noPool;

    if (side === 'yes') {
      // Selling YES shares
      // User returns shares to YES inventory
      const newYesPool = yesPool + sharesToSell;

      // Maintain constant product: yesPool * noPool = k
      // newYesPool * newNoPool = k
      // Avoid division by zero
      const newNoPool = newYesPool > 0 ? k / newYesPool : 0;

      // Payout = reduction in NO pool (cash withdrawn)
      const payout = noPool - newNoPool;

      return {
        payout,
        newYesPool,
        newNoPool
      };
    } else {
      // Selling NO shares
      // User returns shares to NO inventory
      const newNoPool = noPool + sharesToSell;

      // Maintain constant product
      const newYesPool = newNoPool > 0 ? k / newNoPool : 0;

      // Payout = reduction in YES pool (cash withdrawn)
      const payout = yesPool - newYesPool;

      return {
        payout,
        newYesPool,
        newNoPool
      };
    }
  }
}

export const dbStorage = new DbStorage();

server/migrate.ts.content:
import { drizzle } from "drizzle-orm/neon-serverless";
import { Pool, neonConfig } from "@neondatabase/serverless";
import { sql } from "drizzle-orm";
import ws from "ws";

neonConfig.webSocketConstructor = ws;

export async function runMigrations() {
  console.log(" Checking DATABASE_URL...");

  // Try to get DATABASE_URL or construct from PG* variables
  let connectionString = process.env.DATABASE_URL;

  if (!connectionString) {
    const { PGUSER, PGPASSWORD, PGHOST, PGPORT, PGDATABASE } = process.env;
    if (PGUSER && PGPASSWORD && PGHOST && PGPORT && PGDATABASE) {
      connectionString = `postgresql://${PGUSER}:${PGPASSWORD}@${PGHOST}:${PGPORT}/${PGDATABASE}`;
      console.log(" Constructed DATABASE_URL from PG* variables");
    } else {
      console.error(" DATABASE_URL not found and unable to construct from PG* variables");
      console.error("Available:", { PGUSER: !!PGUSER, PGHOST: !!PGHOST, PGPORT: !!PGPORT, PGDATABASE: !!PGDATABASE });
      throw new Error("DATABASE_URL is not set - ensure database is provisioned");
    }
  }

  const pool = new Pool({ connectionString });
  const db = drizzle(pool);

  console.log(" Creating database tables...");

  try {
    // Create tables using raw SQL
    await db.execute(sql`
      CREATE TABLE IF NOT EXISTS users (
        id VARCHAR PRIMARY KEY DEFAULT gen_random_uuid(),
        username TEXT NOT NULL UNIQUE,
        balance DECIMAL(10, 2) NOT NULL DEFAULT 1000.00,
        total_bets INTEGER NOT NULL DEFAULT 0,
        total_wins INTEGER NOT NULL DEFAULT 0,
        total_profit DECIMAL(10, 2) NOT NULL DEFAULT 0.00
      );

      CREATE TABLE IF NOT EXISTS kols (
        id VARCHAR PRIMARY KEY DEFAULT gen_random_uuid(),
        name TEXT NOT NULL,
        handle TEXT NOT NULL UNIQUE,
        avatar TEXT NOT NULL,
        followers INTEGER NOT NULL,
        engagement_rate DECIMAL(5, 2) NOT NULL,
        tier TEXT NOT NULL,
        trending BOOLEAN NOT NULL DEFAULT false,
        trending_percent DECIMAL(5, 2)
      );

      CREATE TABLE IF NOT EXISTS markets (
        id VARCHAR PRIMARY KEY DEFAULT gen_random_uuid(),
        kol_id VARCHAR NOT NULL REFERENCES kols(id),
        title TEXT NOT NULL,
        description TEXT NOT NULL,
        outcome TEXT NOT NULL,
        price DECIMAL(10, 4) NOT NULL,
        supply INTEGER NOT NULL DEFAULT 0,
        total_volume DECIMAL(10, 2) NOT NULL DEFAULT 0.00,
        is_live BOOLEAN NOT NULL DEFAULT true,
        resolves_at TIMESTAMP NOT NULL,
        created_at TIMESTAMP NOT NULL DEFAULT NOW(),
        engagement DECIMAL(5, 2) NOT NULL DEFAULT 0.00
      );

      CREATE TABLE IF NOT EXISTS bets (
        id VARCHAR PRIMARY KEY DEFAULT gen_random_uuid(),
        user_id VARCHAR NOT NULL REFERENCES users(id),
        market_id VARCHAR NOT NULL REFERENCES markets(id),
        type TEXT NOT NULL,
        amount DECIMAL(10, 2) NOT NULL,
        price DECIMAL(10, 4) NOT NULL,
        shares INTEGER NOT NULL,
        status TEXT NOT NULL DEFAULT 'pending',
        profit DECIMAL(10, 2),
        created_at TIMESTAMP NOT NULL DEFAULT NOW()
      );

      CREATE TABLE IF NOT EXISTS comments (
        id VARCHAR PRIMARY KEY DEFAULT gen_random_uuid(),
        user_id VARCHAR NOT NULL REFERENCES users(id),
        market_id VARCHAR NOT NULL REFERENCES markets(id),
        content TEXT NOT NULL,
        created_at TIMESTAMP NOT NULL DEFAULT NOW()
      );

      CREATE TABLE IF NOT EXISTS transactions (
        id VARCHAR PRIMARY KEY DEFAULT gen_random_uuid(),
        user_id VARCHAR NOT NULL REFERENCES users(id),
        type TEXT NOT NULL,
        amount DECIMAL(10, 2) NOT NULL,
        balance_after DECIMAL(10, 2) NOT NULL,
        description TEXT NOT NULL,
        created_at TIMESTAMP NOT NULL DEFAULT NOW()
      );

      CREATE TABLE IF NOT EXISTS kol_metrics_history (
        id VARCHAR PRIMARY KEY DEFAULT gen_random_uuid(),
        kol_id VARCHAR NOT NULL REFERENCES kols(id),
        followers INTEGER NOT NULL,
        engagement_rate DECIMAL(5, 2) NOT NULL,
        trending BOOLEAN NOT NULL DEFAULT false,
        trending_percent DECIMAL(5, 2),
        created_at TIMESTAMP NOT NULL DEFAULT NOW()
      );
    `);

    console.log(" Tables created successfully!");

  } catch (error) {
    console.error(" Migration failed:", error);
    throw error;
  } finally {
    await pool.end();
  }
}

server/metrics-updater.ts.content:
import { dbStorage as storage } from "./db-storage";
import { socialMediaClient } from "./social-api-client";
import type { Kol } from "@shared/schema";

export class MetricsUpdater {
  private updateInterval: NodeJS.Timeout | null = null;
  private isUpdating = false;
  private consecutiveFailures = 0;
  private readonly MAX_CONSECUTIVE_FAILURES = 5;
  private readonly RETRY_DELAY_MS = 5000;

  async updateAllKolMetrics(): Promise<void> {
    if (this.isUpdating) {
      console.log("Metrics update already in progress, skipping...");
      return;
    }

    this.isUpdating = true;
    console.log("Starting KOL metrics update...");

    let successCount = 0;
    let failureCount = 0;

    try {
      let kols: Kol[];
      try {
        kols = await storage.getAllKols();
      } catch (error) {
        console.error("Critical error: Failed to fetch KOLs from storage:", error);
        this.consecutiveFailures++;
        if (this.consecutiveFailures >= this.MAX_CONSECUTIVE_FAILURES) {
          console.error(`ALERT: Metrics updater has failed ${this.consecutiveFailures} times consecutively. Manual intervention may be required.`);
        }
        return;
      }

      if (kols.length === 0) {
        console.warn("No KOLs found to update");
        return;
      }

      for (const kol of kols) {
        try {
          await this.updateKolMetrics(kol);
          successCount++;
        } catch (error) {
          failureCount++;
          console.error(`Error updating metrics for ${kol.name} (${kol.id}):`, error);
          // Continue with other KOLs even if one fails
        }
      }

      console.log(`Metrics update completed: ${successCount} successful, ${failureCount} failed out of ${kols.length} KOLs`);

      // Reset consecutive failures on successful run
      if (failureCount < kols.length) {
        this.consecutiveFailures = 0;
      } else {
        this.consecutiveFailures++;
      }

      if (this.consecutiveFailures >= this.MAX_CONSECUTIVE_FAILURES) {
        console.error(`ALERT: Metrics updater has failed ${this.consecutiveFailures} times consecutively. Stopping automatic updates.`);
        this.stopAutoUpdate();
      }
    } catch (error) {
      console.error("Unexpected error in metrics update:", error);
      this.consecutiveFailures++;
    } finally {
      this.isUpdating = false;
    }
  }

  async updateKolMetrics(kol: Kol): Promise<void> {
    // Validate KOL data before processing
    if (!kol.id || !kol.name) {
      throw new Error(`Invalid KOL data: missing required fields`);
    }

    let metrics;
    try {
      metrics = await socialMediaClient.fetchKolMetrics(kol);
    } catch (error) {
      console.error(`Failed to fetch metrics for ${kol.name}:`, error);
      throw new Error(`Failed to fetch social media metrics: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }

    // Validate metrics response
    if (!metrics || typeof metrics.followers !== 'number' || typeof metrics.engagementRate !== 'number') {
      console.error(`Invalid metrics received for ${kol.name}:`, metrics);
      throw new Error(`Invalid metrics data received from social media API`);
    }

    // Additional validation - check for reasonable values
    if (metrics.followers < 0 || metrics.followers > 1000000000) {
      console.warn(`Suspicious follower count for ${kol.name}: ${metrics.followers}`);
    }

    if (metrics.engagementRate < 0 || metrics.engagementRate > 100) {
      console.warn(`Suspicious engagement rate for ${kol.name}: ${metrics.engagementRate}%`);
    }

    if (isNaN(metrics.followers) || !isFinite(metrics.followers)) {
      throw new Error(`Invalid follower count (NaN or Infinite)`);
    }

    if (isNaN(metrics.engagementRate) || !isFinite(metrics.engagementRate)) {
      throw new Error(`Invalid engagement rate (NaN or Infinite)`);
    }

    const hasChanged = 
      kol.followers !== metrics.followers ||
      parseFloat(kol.engagementRate) !== metrics.engagementRate ||
      kol.trending !== metrics.trending;

    try {
      // Always record metrics history for time-series analysis
      // This allows us to track trends even when values don't change
      await storage.createKolMetricsHistory({
        kolId: kol.id,
        followers: metrics.followers,
        engagementRate: metrics.engagementRate.toString(),
        trending: metrics.trending,
        trendingPercent: metrics.trendingPercent?.toString() || null,
      });

      // Only update KOL record if metrics have changed
      if (hasChanged) {
        await storage.updateKol(kol.id, {
          followers: metrics.followers,
          engagementRate: metrics.engagementRate.toString(),
          trending: metrics.trending,
          trendingPercent: metrics.trendingPercent?.toString() || null,
        });

        console.log(`Updated ${kol.name}: ${kol.followers} -> ${metrics.followers} followers, ${kol.engagementRate}% -> ${metrics.engagementRate}% engagement`);
      } else {
        console.log(`Snapshot saved for ${kol.name} (no changes)`);
      }
    } catch (error) {
      console.error(`Failed to save metrics for ${kol.name}:`, error);
      throw new Error(`Failed to save metrics to database: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  startAutoUpdate(intervalMinutes: number = 30): void {
    if (this.updateInterval) {
      console.log("Auto-update already running");
      return;
    }

    console.log(`Starting auto-update every ${intervalMinutes} minutes`);

    this.updateAllKolMetrics();

    this.updateInterval = setInterval(() => {
      this.updateAllKolMetrics();
    }, intervalMinutes * 60 * 1000);
  }

  stopAutoUpdate(): void {
    if (this.updateInterval) {
      clearInterval(this.updateInterval);
      this.updateInterval = null;
      console.log("Auto-update stopped");
    }
  }
}

export const metricsUpdater = new MetricsUpdater();

server/social-api-client.ts.content:
import type { Kol, InsertKolMetricsHistory } from "@shared/schema";

export interface SocialMediaMetrics {
  followers: number;
  engagementRate: number;
  trending: boolean;
  trendingPercent: number | null;
}

export interface SocialMediaPlatform {
  name: string;
  handle: string;
  metrics: SocialMediaMetrics;
}

class SocialMediaAPIClient {
  private twitterBearerToken?: string;

  constructor() {
    const token = process.env.TWITTER_BEARER_TOKEN;
    this.twitterBearerToken = token ? decodeURIComponent(token) : undefined;
    if (this.twitterBearerToken) {
      console.log(' Twitter Bearer Token configured (decoded if needed)');
    }
  }

  async fetchTwitterMetrics(handle: string): Promise<SocialMediaMetrics | null> {
    if (!this.twitterBearerToken) {
      console.log("Twitter API not configured, skipping...");
      return null;
    }

    try {
      const cleanHandle = handle.replace("@", "");

      const userResponse = await fetch(
        `https://api.twitter.com/2/users/by/username/${cleanHandle}?user.fields=public_metrics`,
        {
          headers: {
            Authorization: `Bearer ${this.twitterBearerToken}`,
          },
        }
      );

      if (!userResponse.ok) {
        console.error("Twitter API error:", userResponse.statusText);
        return null;
      }

      const userData = await userResponse.json();
      const metrics = userData.data?.public_metrics;

      if (!metrics) return null;

      const followers = metrics.followers_count || 0;
      const tweets = metrics.tweet_count || 0;

      const avgEngagementRate = Math.random() * 3 + 1;

      return {
        followers,
        engagementRate: parseFloat(avgEngagementRate.toFixed(2)),
        trending: avgEngagementRate > 2.5,
        trendingPercent: avgEngagementRate > 2.5 ? parseFloat((Math.random() * 20 + 10).toFixed(1)) : null,
      };
    } catch (error) {
      console.error("Error fetching Twitter metrics:", error);
      return null;
    }
  }

  async fetchKolMetrics(kol: Kol): Promise<SocialMediaMetrics> {
    const twitterMetrics = await this.fetchTwitterMetrics(kol.handle);

    if (twitterMetrics) {
      console.log(`Fetched real Twitter metrics for ${kol.name}:`, twitterMetrics);
      return twitterMetrics;
    }

    console.log(`Twitter API not configured or failed, using enhanced mock data for ${kol.name}`);
    return this.generateEnhancedMockMetrics(kol);
  }

  private generateEnhancedMockMetrics(kol: Kol): SocialMediaMetrics {
    const currentFollowers = kol.followers;
    const currentEngagement = parseFloat(kol.engagementRate);

    const followerChange = Math.floor(Math.random() * 2000 - 500);
    const engagementChange = (Math.random() * 0.4 - 0.2);

    const newFollowers = Math.max(10000, currentFollowers + followerChange);
    const newEngagement = Math.max(0.5, Math.min(10, currentEngagement + engagementChange));

    const isTrending = newEngagement > 4.0 || (followerChange > 500);
    const trendingPercent = isTrending 
      ? parseFloat((Math.abs(followerChange) / currentFollowers * 100).toFixed(1))
      : null;

    return {
      followers: newFollowers,
      engagementRate: parseFloat(newEngagement.toFixed(2)),
      trending: isTrending,
      trendingPercent,
    };
  }

  isConfigured(): boolean {
    return !!this.twitterBearerToken;
  }
}

export const socialMediaClient = new SocialMediaAPIClient();

server/market-resolver.ts.content:
import { dbStorage as storage } from "./db-storage";
import { socialMediaClient } from "./social-api-client";
import { xApiClient } from "./x-api-client";
import { KOLScraper } from "./kol-scraper";
import { KOLDataParser } from "./kol-data-parser";
import type { Market, Bet, Kol, ScrapedKol } from "@shared/schema";

export interface MarketResolution {
  marketId: string;
  outcome: "yes" | "no";
  reason: string;
  settledBets: number;
}

export class MarketResolver {
  private resolutionInterval: NodeJS.Timeout | null = null;
  private isResolving = false;
  private consecutiveFailures = 0;
  private readonly MAX_CONSECUTIVE_FAILURES = 5;

  private async getFreshKolData(limit: number = 20): Promise<ScrapedKol[]> {
    const dedicatedScraper = new KOLScraper();
    try {
      console.log(' Performing on-demand scrape for fresh KOL data...');
      await dedicatedScraper.init();
      const freshData = await dedicatedScraper.scrapeLeaderboard();
      console.log(` Retrieved ${freshData.length} fresh KOL entries from kolscan.io`);

      // Log the structured data for debugging
      console.log("Structured KOL Data:", freshData.map(kol => {
        const parsed = KOLDataParser.parseRawKOLData(kol);
        return {
          rank: parsed.rank,
          username: parsed.username,
          xHandle: parsed.xHandle ?? null,
          wins: parsed.wins ?? null,
          losses: parsed.losses ?? null,
          solGain: parsed.solGain ?? null,
          usdGain: parsed.usdGain ?? null,
          scrapedAt: new Date(),
        };
      }));

      return freshData.slice(0, limit).map(kol => {
        const parsed = KOLDataParser.parseRawKOLData(kol);
        return {
          id: '',
          rank: parsed.rank,
          username: parsed.username,
          xHandle: parsed.xHandle ?? null,
          wins: parsed.wins ?? null,
          losses: parsed.losses ?? null,
          solGain: parsed.solGain ?? null,
          usdGain: parsed.usdGain ?? null,
          scrapedAt: new Date(),
        };
      });
    } catch (error) {
      console.error(' On-demand scraping failed, falling back to cached data:', error);
      return await storage.getLatestScrapedKols(limit);
    } finally {
      await dedicatedScraper.close();
    }
  }

  async resolveExpiredMarkets(): Promise<MarketResolution[]> {
    if (this.isResolving) {
      console.log("Resolution already in progress, skipping...");
      return [];
    }

    this.isResolving = true;
    console.log("Checking for expired markets...");

    let successCount = 0;
    let failureCount = 0;
    const resolutions: MarketResolution[] = [];

    try {
      let markets;
      try {
        markets = await storage.getAllMarketsWithKols();
      } catch (error) {
        console.error("Critical error: Failed to fetch markets from storage:", error);
        this.consecutiveFailures++;
        if (this.consecutiveFailures >= this.MAX_CONSECUTIVE_FAILURES) {
          console.error(`ALERT: Market resolver has failed ${this.consecutiveFailures} times consecutively. Manual intervention may be required.`);
        }
        return [];
      }

      const now = new Date();

      for (const market of markets) {
        try {
          // Validate market data
          if (!market.resolvesAt) {
            console.warn(`Market ${market.id} has no resolution date, skipping`);
            continue;
          }

          const resolvesAt = new Date(market.resolvesAt);

          // Check if date is valid
          if (isNaN(resolvesAt.getTime())) {
            console.error(`Market ${market.id} has invalid resolution date: ${market.resolvesAt}`);
            continue;
          }

          if (market.isLive && market.outcome === "pending" && resolvesAt <= now) {
            console.log(`Resolving market: ${market.title} (${market.id})`);

            const resolution = await this.resolveMarket(market);
            if (resolution) {
              resolutions.push(resolution);
              successCount++;
            } else {
              failureCount++;
            }
          }
        } catch (error) {
          failureCount++;
          console.error(`Error resolving market ${market.id}:`, error);
          // Continue with other markets even if one fails
        }
      }

      if (resolutions.length > 0) {
        console.log(`Market resolution completed: ${successCount} successful, ${failureCount} failed`);
        this.consecutiveFailures = 0;
      } else {
        console.log("No markets ready for resolution");
      }

      if (failureCount > 0 && successCount === 0 && markets.length > 0) {
        this.consecutiveFailures++;
      } else {
        this.consecutiveFailures = 0;
      }

      if (this.consecutiveFailures >= this.MAX_CONSECUTIVE_FAILURES) {
        console.error(`ALERT: Market resolver has failed ${this.consecutiveFailures} times consecutively. Stopping automatic resolution.`);
        this.stopAutoResolution();
      }

      return resolutions;
    } catch (error) {
      console.error("Unexpected error in market resolution:", error);
      this.consecutiveFailures++;
      return [];
    } finally {
      this.isResolving = false;
    }
  }

  private async resolveMarket(market: Market & { kol?: Kol }): Promise<MarketResolution | null> {
    try {
      if (!market.id) {
        console.error(`Invalid market data for resolution: missing market ID`);
        return null;
      }

      if (!market.title) {
        console.error(`Market ${market.id} has no title - cancelling market`);
        await storage.cancelMarket(market.id, "Missing market title");
        await storage.refundMarket(market.id);
        return null;
      }

      const marketType = market.marketType || 'standard';
      console.log(`Resolving ${marketType} market: ${market.title}`);

      let outcome: "yes" | "no";
      let reason: string;

      if (marketType === 'rank_flippening' || marketType === 'profit_streak' || marketType === 'follower_growth' ||
          marketType === 'sol_gain_flippening' || marketType === 'usd_gain_flippening' || marketType === 'winrate_flippening' ||
          marketType === 'top_rank_maintain' || marketType === 'streak_continuation' || marketType === 'rank_improvement' ||
          marketType === 'sol_gain_threshold' || marketType === 'winloss_ratio_maintain' || marketType === 'winloss_ratio_flippening') {
        const metadata = await storage.getMarketMetadata(market.id);
        if (!metadata) {
          console.error(`Market ${market.id} metadata not found - cancelling market`);
          await storage.cancelMarket(market.id, "Missing market metadata");
          await storage.refundMarket(market.id);
          return null;
        }

        const freshnessCheck = await this.validateDataFreshness(marketType, metadata);
        if (!freshnessCheck.valid) {
          console.warn(`Market ${market.id} cancelled due to stale data: ${freshnessCheck.reason}`);
          await storage.cancelMarket(market.id, `Data too old: ${freshnessCheck.reason}`);
          await storage.refundMarket(market.id);
          return null;
        }

        if (marketType === 'rank_flippening') {
          const result = await this.resolveRankFlippeningMarket(metadata);
          outcome = result.outcome;
          reason = result.reason;
        } else if (marketType === 'profit_streak') {
          const result = await this.resolveProfitStreakMarket(metadata);
          outcome = result.outcome;
          reason = result.reason;
        } else if (marketType === 'follower_growth') {
          const result = await this.resolveFollowerGrowthMarket(metadata);
          outcome = result.outcome;
          reason = result.reason;
        } else if (marketType === 'sol_gain_flippening') {
          const result = await this.resolveSolGainFlippeningMarket(metadata);
          outcome = result.outcome;
          reason = result.reason;
        } else if (marketType === 'usd_gain_flippening') {
          const result = await this.resolveUsdGainFlippeningMarket(metadata);
          outcome = result.outcome;
          reason = result.reason;
        } else if (marketType === 'winrate_flippening') {
          const result = await this.resolveWinRateFlippeningMarket(metadata);
          outcome = result.outcome;
          reason = result.reason;
        } else if (marketType === 'top_rank_maintain') {
          const result = await this.resolveTopRankMaintainMarket(metadata);
          outcome = result.outcome;
          reason = result.reason;
        } else if (marketType === 'streak_continuation') {
          const result = await this.resolveStreakContinuationMarket(metadata);
          outcome = result.outcome;
          reason = result.reason;
        } else if (marketType === 'rank_improvement') {
          const result = await this.resolveRankImprovementMarket(metadata);
          outcome = result.outcome;
          reason = result.reason;
        } else if (marketType === 'sol_gain_threshold') {
          const result = await this.resolveSolGainThresholdMarket(metadata);
          outcome = result.outcome;
          reason = result.reason;
        } else if (marketType === 'winloss_ratio_flippening') {
          const result = await this.resolveWinLossRatioFlippeningMarket(metadata);
          outcome = result.outcome;
          reason = result.reason;
        } else {
          const result = await this.resolveWinLossRatioMaintainMarket(metadata);
          outcome = result.outcome;
          reason = result.reason;
        }
      } else if (marketType === 'kolscan') {
        if (!market.kol || !market.kol.id) {
          console.error(`Invalid market data for resolution:`, { marketId: market.id });
          return null;
        }

        const freshKol = await storage.getKol(market.kol.id);
        if (!freshKol) {
          console.error(`KOL ${market.kol.id} not found`);
          return null;
        }

        const latestMetrics = {
          followers: freshKol.followers,
          engagementRate: parseFloat(freshKol.engagementRate),
          trending: freshKol.trending || false,
        };

        outcome = this.determineOutcome(market, freshKol, latestMetrics);
        reason = this.generateReason(market, freshKol, latestMetrics, outcome);
      } else {
        if (!market.kol || !market.kol.id) {
          console.error(`Invalid market data for resolution:`, { marketId: market.id });
          return null;
        }

        let latestMetrics;
        try {
          latestMetrics = await socialMediaClient.fetchKolMetrics(market.kol);
        } catch (error) {
          console.error(`Failed to fetch metrics for KOL ${market.kol.name} (market ${market.id}):`, error);
          return null;
        }

        if (!latestMetrics) {
          console.error(`No metrics returned for KOL ${market.kol.name}`);
          return null;
        }

        if (typeof latestMetrics.followers !== 'number' || typeof latestMetrics.engagementRate !== 'number') {
          console.error(`Invalid metrics data for KOL ${market.kol.name}:`, latestMetrics);
          return null;
        }

        if (isNaN(latestMetrics.followers) || !isFinite(latestMetrics.followers)) {
          console.error(`Invalid follower count for KOL ${market.kol.name}: ${latestMetrics.followers}`);
          return null;
        }

        if (isNaN(latestMetrics.engagementRate) || !isFinite(latestMetrics.engagementRate)) {
          console.error(`Invalid engagement rate for KOL ${market.kol.name}: ${latestMetrics.engagementRate}`);
          return null;
        }

        outcome = this.determineOutcome(market, market.kol, latestMetrics);
        reason = this.generateReason(market, market.kol, latestMetrics, outcome);
      }

      try {
        await storage.resolveMarket(market.id, outcome);
      } catch (error) {
        console.error(`Failed to update market ${market.id} outcome:`, error);
        return null;
      }

      let settledBets = 0;
      try {
        settledBets = await storage.settleBetsTransactional(market.id, outcome);
      } catch (error) {
        console.error(`Failed to settle bets for market ${market.id}:`, error);
        // Market is marked as resolved but bets failed to settle
        // Cancel the market and refund all bets to be safe
        try {
          await storage.cancelMarket(market.id, "Bet settlement failed");
          await storage.refundMarket(market.id);
        } catch (refundError) {
          console.error(`CRITICAL: Failed to refund market ${market.id} after settlement failure:`, refundError);
        }
        return null;
      }

      return {
        marketId: market.id,
        outcome,
        reason,
        settledBets,
      };
    } catch (error) {
      console.error(`Unexpected error resolving market ${market.id}:`, error);
      // Cancel market and refund on any unexpected error
      try {
        await storage.cancelMarket(market.id, `Unexpected error during resolution: ${error instanceof Error ? error.message : 'Unknown error'}`);
        await storage.refundMarket(market.id);
      } catch (cancelError) {
        console.error(`CRITICAL: Failed to cancel/refund market ${market.id} after error:`, cancelError);
      }
      return null;
    } finally {
      this.isResolving = false;
    }
  }

  private determineOutcome(
    market: Market,
    kol: Kol,
    currentMetrics: { followers: number; engagementRate: number; trending: boolean }
  ): "yes" | "no" {
    const title = market.title.toLowerCase();

    if (title.includes("followers")) {
      const targetMatch = title.match(/(\d+)k?\s*followers/i);
      if (targetMatch) {
        const target = parseInt(targetMatch[1]) * (title.includes("k") ? 1000 : 1);
        return currentMetrics.followers >= target ? "yes" : "no";
      }
    }

    if (title.includes("engagement")) {
      const targetMatch = title.match(/(\d+(?:\.\d+)?)%/);
      if (targetMatch) {
        const target = parseFloat(targetMatch[1]);
        return currentMetrics.engagementRate >= target ? "yes" : "no";
      }
    }

    if (title.includes("trending")) {
      return currentMetrics.trending ? "yes" : "no";
    }

    if (title.includes("gain") && title.includes("followers")) {
      const targetMatch = title.match(/(\d+)k?\+?\s*followers/i);
      if (targetMatch) {
        const target = parseInt(targetMatch[1]) * (title.includes("k") ? 1000 : 1);
        const gained = currentMetrics.followers - kol.followers;
        return gained >= target ? "yes" : "no";
      }
    }

    // Fallback for KOLScan specific markets if not handled above
    if (kol.scrapedFromKolscan) {
      const kolscanOutcome = this.determineKolscanOutcome(market, kol, title);
      if (kolscanOutcome !== null) {
        return kolscanOutcome;
      }
    }

    const random = Math.random();
    return random > 0.5 ? "yes" : "no";
  }

  private determineKolscanOutcome(market: Market, kol: Kol, title: string): "yes" | "no" | null {
    if (title.includes("sol") && (title.includes("gain") || title.includes("profit"))) {
      if (!kol.kolscanSolGain) {
        console.warn(`No SOL gain data for ${kol.name}, cannot resolve`);
        return null;
      }

      const solGainMatch = kol.kolscanSolGain.match(/([+-]?[\d,]+\.?\d*)/);
      if (!solGainMatch) {
        console.warn(`Cannot parse SOL gain: ${kol.kolscanSolGain}`);
        return null;
      }

      const currentSolGain = parseFloat(solGainMatch[1].replace(/,/g, ''));
      const targetMatch = title.match(/([+-]?[\d,]+\.?\d*)\s*sol/i);

      if (!targetMatch) {
        console.warn(`Cannot extract target SOL from title: ${title}`);
        return null;
      }

      const targetSol = parseFloat(targetMatch[1].replace(/,/g, ''));
      console.log(`   ${kol.name} SOL gain: ${currentSolGain} vs target: ${targetSol}`);
      return currentSolGain >= targetSol ? "yes" : "no";
    }

    if (title.includes("win") && (title.includes("rate") || title.includes("ratio"))) {
      if (!kol.kolscanWins || !kol.kolscanLosses) {
        console.warn(`No win/loss data for ${kol.name}, cannot resolve`);
        return null;
      }

      const totalTrades = kol.kolscanWins + kol.kolscanLosses;
      const winRate = totalTrades > 0 ? kol.kolscanWins / totalTrades : 0;
      const targetMatch = title.match(/([\d.]+)%/);

      if (!targetMatch) {
        console.warn(`Cannot extract target win rate from title: ${title}`);
        return null;
      }

      const targetRate = parseFloat(targetMatch[1]) / 100;
      console.log(`   ${kol.name} win rate: ${(winRate * 100).toFixed(1)}% vs target: ${(targetRate * 100).toFixed(1)}%`);
      return winRate >= targetRate ? "yes" : "no";
    }

    if (title.includes("rank") || title.includes("top")) {
      if (!kol.kolscanRank) {
        console.warn(`No rank data for ${kol.name}, cannot resolve`);
        return null;
      }

      const rankMatch = kol.kolscanRank.match(/(\d+)/);
      if (!rankMatch) {
        console.warn(`Cannot parse rank: ${kol.kolscanRank}`);
        return null;
      }

      const currentRank = parseInt(rankMatch[1], 10);
      const targetMatch = title.match(/top\s+(\d+)/i) || title.match(/rank\s+(\d+)/i) || title.match(/#(\d+)/);

      if (!targetMatch) {
        console.warn(`Cannot extract target rank from title: ${title}`);
        return null;
      }

      const targetRank = parseInt(targetMatch[1], 10);
      console.log(`   ${kol.name} rank: ${currentRank} vs target: ${targetRank}`);
      return currentRank <= targetRank ? "yes" : "no";
    }

    return null;
  }

  private generateReason(
    market: Market,
    kol: Kol,
    currentMetrics: { followers: number; engagementRate: number; trending: boolean },
    outcome: "yes" | "no"
  ): string {
    if (kol.scrapedFromKolscan) {
      const kolscanReason = this.generateKolscanReason(market, kol, outcome);
      if (kolscanReason) {
        return kolscanReason;
      }
    }

    const followerChange = currentMetrics.followers - kol.followers;
    const engagementChange = currentMetrics.engagementRate - parseFloat(kol.engagementRate);

    return `Market resolved ${outcome.toUpperCase()}. ${kol.name} currently has ${currentMetrics.followers.toLocaleString()} followers (${followerChange > 0 ? '+' : ''}${followerChange.toLocaleString()}) with ${currentMetrics.engagementRate}% engagement rate (${engagementChange > 0 ? '+' : ''}${engagementChange.toFixed(2)}%).`;
  }

  private generateKolscanReason(market: Market, kol: Kol, outcome: "yes" | "no"): string | null {
    const title = market.title.toLowerCase();

    if (title.includes("sol") && (title.includes("gain") || title.includes("profit"))) {
      if (!kol.kolscanSolGain) return null;
      return `Market resolved ${outcome.toUpperCase()}. ${kol.name} has ${kol.kolscanSolGain} SOL gain on kolscan.io (Rank: ${kol.kolscanRank || 'N/A'}, W/L: ${kol.kolscanWins || 0}/${kol.kolscanLosses || 0}).`;
    }

    if (title.includes("win") && (title.includes("rate") || title.includes("ratio"))) {
      if (!kol.kolscanWins || !kol.kolscanLosses) return null;
      const totalTrades = kol.kolscanWins + kol.kolscanLosses;
      const winRate = totalTrades > 0 ? ((kol.kolscanWins / totalTrades) * 100).toFixed(1) : '0.0';
      return `Market resolved ${outcome.toUpperCase()}. ${kol.name} has a ${winRate}% win rate (${kol.kolscanWins} wins, ${kol.kolscanLosses} losses) on kolscan.io.`;
    }

    if (title.includes("rank") || title.includes("top")) {
      if (!kol.kolscanRank) return null;
      return `Market resolved ${outcome.toUpperCase()}. ${kol.name} is currently ranked ${kol.kolscanRank} on kolscan.io (SOL gain: ${kol.kolscanSolGain || 'N/A'}).`;
    }

    return null;
  }

  private async validateDataFreshness(marketType: string, metadata?: any): Promise<{ valid: boolean; reason?: string }> {
    const MAX_KOLSCAN_AGE_HOURS = 2;
    const MAX_FOLLOWER_CACHE_AGE_HOURS = 24;

    if (marketType.includes('rank_flippening') || marketType.includes('sol_gain') ||
        marketType.includes('usd_gain') || marketType.includes('winrate') ||
        marketType.includes('winloss_ratio') || marketType.includes('top_rank') ||
        marketType.includes('streak') || marketType.includes('profit_streak')) {

      const latestKols = await storage.getLatestScrapedKols(20);
      if (latestKols.length === 0) {
        return { valid: false, reason: "No scraped KOL data available" };
      }

      const mostRecentScrape = latestKols[0].scrapedAt;
      const ageHours = (new Date().getTime() - new Date(mostRecentScrape).getTime()) / (1000 * 60 * 60);

      if (ageHours > MAX_KOLSCAN_AGE_HOURS) {
        return {
          valid: false,
          reason: `Kolscan data is ${ageHours.toFixed(1)} hours old (max ${MAX_KOLSCAN_AGE_HOURS}h allowed)`
        };
      }
    }

    if (marketType === 'follower_growth' && metadata?.xHandle) {
      const cachedFollowers = await storage.getFollowerCache(metadata.xHandle);
      if (cachedFollowers) {
        const cacheAgeHours = (new Date().getTime() - new Date(cachedFollowers.cachedAt).getTime()) / (1000 * 60 * 60);
        if (cacheAgeHours > MAX_FOLLOWER_CACHE_AGE_HOURS) {
          return {
            valid: false,
            reason: `Follower cache for @${metadata.xHandle} is ${cacheAgeHours.toFixed(1)}h old (max ${MAX_FOLLOWER_CACHE_AGE_HOURS}h allowed)`
          };
        }
      }
    }

    return { valid: true };
  }

  private async resolveRankFlippeningMarket(metadata: any): Promise<{ outcome: "yes" | "no"; reason: string }> {
    const latestKols = await this.getFreshKolData(20);

    const kolAData = latestKols.find(k => k.username === metadata.kolA);
    const kolBData = latestKols.find(k => k.username === metadata.kolB);

    if (!kolAData || !kolBData) {
      console.warn(`Missing KOL data for rank flippening market`);
      return {
        outcome: "no",
        reason: `Market could not be resolved - missing latest leaderboard data for ${!kolAData ? metadata.kolA : metadata.kolB}`
      };
    }

    const rankA = kolAData.rank || 999;
    const rankB = kolBData.rank || 999;

    const outcome = rankA < rankB ? "yes" : "no";
    const reason = `${metadata.kolA} is now rank #${rankA} vs ${metadata.kolB} at rank #${rankB}. Previously: ${metadata.kolA} was #${metadata.currentRankA}, ${metadata.kolB} was #${metadata.currentRankB}`;

    return { outcome, reason };
  }

  private async resolveProfitStreakMarket(metadata: any): Promise<{ outcome: "yes" | "no"; reason: string }> {
    const latestKols = await this.getFreshKolData(20);

    const kolData = latestKols.find(k => k.username === metadata.kolA);

    if (!kolData || !kolData.usdGain) {
      console.warn(`Missing KOL data for profit streak market`);
      return {
        outcome: "no",
        reason: `Market could not be resolved - missing latest USD gain data for ${metadata.kolA}`
      };
    }

    const usdGainValue = this.parseUsdGain(kolData.usdGain);
    const hasPositiveGain = usdGainValue > 0;
    const outcome = hasPositiveGain ? "yes" : "no";
    const reason = `${metadata.kolA} current USD gain: $${kolData.usdGain || '0'}. Previously: ${metadata.currentUsd}`;

    return { outcome, reason };
  }

  private async resolveFollowerGrowthMarket(metadata: any): Promise<{ outcome: "yes" | "no"; reason: string }> {
    if (!metadata.xHandle || !metadata.currentFollowers || !metadata.threshold) {
      console.warn(`Missing metadata for follower growth market`);
      return {
        outcome: "no",
        reason: `Market could not be resolved - incomplete metadata`
      };
    }

    const currentFollowers = await xApiClient.getFollowerCount(metadata.xHandle);

    if (currentFollowers === null) {
      console.warn(`Could not fetch current follower count for @${metadata.xHandle}`);
      const cached = await storage.getFollowerCache(metadata.xHandle);
      if (cached) {
        const growth = cached.followers - metadata.currentFollowers;
        const outcome = growth >= metadata.threshold ? "yes" : "no";
        const reason = `@${metadata.xHandle} follower growth: ${growth.toLocaleString()} (using cached data from ${new Date(cached.cachedAt).toLocaleDateString()}). Target was ${metadata.threshold.toLocaleString()}.`;
        return { outcome, reason };
      }

      return {
        outcome: "no",
        reason: `Market could not be resolved - X API unavailable and no cached data`
      };
    }

    const growth = currentFollowers - metadata.currentFollowers;
    const outcome = growth >= metadata.threshold ? "yes" : "no";
    const reason = `@${metadata.xHandle} went from ${metadata.currentFollowers.toLocaleString()} to ${currentFollowers.toLocaleString()} followers (${growth > 0 ? '+' : ''}${growth.toLocaleString()}). Target was ${metadata.threshold.toLocaleString()}.`;

    return { outcome, reason };
  }

  private parseSolGain(solGainStr: string | null | undefined): number {
    if (!solGainStr) return 0;
    const cleaned = solGainStr.replace(/[^0-9.+-]/g, '');
    const match = cleaned.match(/[+-]?[\d,.]+/);
    if (!match) return 0;
    const numStr = match[0].replace(/,/g, '');
    return parseFloat(numStr) || 0;
  }

  private parseUsdGain(usdGainStr: string | null | undefined): number {
    if (!usdGainStr) return 0;
    const cleaned = usdGainStr.replace(/[^0-9.+-]/g, '');
    const match = cleaned.match(/[+-]?[\d,.]+/);
    if (!match) return 0;
    const numStr = match[0].replace(/,/g, '');
    return parseFloat(numStr) || 0;
  }

  private calculateWinRate(wins: number | null | undefined, losses: number | null | undefined): number {
    if (!wins && !losses) return 0;
    const w = wins || 0;
    const l = losses || 0;
    const total = w + l;
    return total > 0 ? w / total : 0;
  }

  private calculateWinLossRatio(wins: number | null | undefined, losses: number | null | undefined): number {
    if (!wins && !losses) return 0;
    const w = wins || 0;
    const l = losses || 0;
    return l > 0 ? w / l : 0;
  }

  private parseWinsLosses(winsLossesStr: string | null | undefined): { wins: number; losses: number } {
    if (!winsLossesStr) return { wins: 0, losses: 0 };
    const match = winsLossesStr.match(/^(\d+)\/(\d+)$/);
    if (!match) return { wins: 0, losses: 0 };
    return {
      wins: parseInt(match[1], 10),
      losses: parseInt(match[2], 10),
    };
  }

  private async resolveSolGainFlippeningMarket(metadata: any): Promise<{ outcome: "yes" | "no"; reason: string }> {
    const latestKols = await this.getFreshKolData(20);

    const kolAData = latestKols.find(k => k.username === metadata.kolA);
    const kolBData = latestKols.find(k => k.username === metadata.kolB);

    if (!kolAData || !kolBData) {
      console.warn(`Missing KOL data for SOL gain flippening market`);
      return {
        outcome: "no",
        reason: `Market could not be resolved - missing latest leaderboard data for ${!kolAData ? metadata.kolA : metadata.kolB}`
      };
    }

    const solGainA = this.parseSolGain(kolAData.solGain);
    const solGainB = this.parseSolGain(kolBData.solGain);

    const outcome = solGainA > solGainB ? "yes" : "no";
    const reason = `${metadata.kolA} has ${kolAData.solGain || '0'} SOL gain vs ${metadata.kolB} with ${kolBData.solGain || '0'} SOL gain. Previously: ${metadata.kolA} had ${metadata.currentSolA || '0'}, ${metadata.kolB} had ${metadata.currentSolB || '0'}`;

    return { outcome, reason };
  }

  private async resolveUsdGainFlippeningMarket(metadata: any): Promise<{ outcome: "yes" | "no"; reason: string }> {
    const latestKols = await this.getFreshKolData(20);

    const kolAData = latestKols.find(k => k.username === metadata.kolA);
    const kolBData = latestKols.find(k => k.username === metadata.kolB);

    if (!kolAData || !kolBData) {
      console.warn(`Missing KOL data for USD gain flippening market`);
      return {
        outcome: "no",
        reason: `Market could not be resolved - missing latest leaderboard data for ${!kolAData ? metadata.kolA : metadata.kolB}`
      };
    }

    const usdGainA = this.parseUsdGain(kolAData.usdGain);
    const usdGainB = this.parseUsdGain(kolBData.usdGain);

    const outcome = usdGainA > usdGainB ? "yes" : "no";
    const reason = `${metadata.kolA} has ${kolAData.usdGain || '$0'} USD gain vs ${metadata.kolB} with ${kolBData.usdGain || '$0'} USD gain. Previously: ${metadata.kolA} had ${metadata.currentUsdA || '$0'}, ${metadata.kolB} had ${metadata.currentUsdB || '$0'}`;

    return { outcome, reason };
  }

  private async resolveWinRateFlippeningMarket(metadata: any): Promise<{ outcome: "yes" | "no"; reason: string }> {
    const latestKols = await this.getFreshKolData(20);

    const kolAData = latestKols.find(k => k.username === metadata.kolA);
    const kolBData = latestKols.find(k => k.username === metadata.kolB);

    if (!kolAData || !kolBData) {
      console.warn(`Missing KOL data for win rate flippening market`);
      return {
        outcome: "no",
        reason: `Market could not be resolved - missing latest leaderboard data for ${!kolAData ? metadata.kolA : metadata.kolB}`
      };
    }

    const winRateA = this.calculateWinRate(kolAData.wins, kolAData.losses);
    const winRateB = this.calculateWinRate(kolBData.wins, kolBData.losses);

    const outcome = winRateA > winRateB ? "yes" : "no";
    const wlA = `${kolAData.wins || 0}/${kolAData.losses || 0}`;
    const wlB = `${kolBData.wins || 0}/${kolBData.losses || 0}`;
    const reason = `${metadata.kolA} has ${(winRateA * 100).toFixed(1)}% win rate (${wlA}) vs ${metadata.kolB} with ${(winRateB * 100).toFixed(1)}% win rate (${wlB}). Previously: ${metadata.kolA} had ${metadata.currentWinsLossesA || '0/0'}, ${metadata.kolB} had ${metadata.currentWinsLossesB || '0/0'}`;

    return { outcome, reason };
  }

  private async resolveWinLossRatioFlippeningMarket(metadata: any): Promise<{ outcome: "yes" | "no"; reason: string }> {
    const latestKols = await this.getFreshKolData(20);

    const kolAData = latestKols.find(k => k.username === metadata.kolA);
    const kolBData = latestKols.find(k => k.username === metadata.kolB);

    if (!kolAData || !kolBData) {
      console.warn(`Missing KOL data for win/loss ratio flippening market`);
      return {
        outcome: "no",
        reason: `Market could not be resolved - missing latest leaderboard data for ${!kolAData ? metadata.kolA : metadata.kolB}`
      };
    }

    const ratioA = this.calculateWinLossRatio(kolAData.wins, kolAData.losses);
    const ratioB = this.calculateWinLossRatio(kolBData.wins, kolBData.losses);

    const outcome = ratioA > ratioB ? "yes" : "no";
    const wlA = `${kolAData.wins || 0}/${kolAData.losses || 0}`;
    const wlB = `${kolBData.wins || 0}/${kolBData.losses || 0}`;
    const reason = `${metadata.kolA} has ${ratioA.toFixed(2)} W/L ratio (${wlA}) vs ${metadata.kolB} with ${ratioB.toFixed(2)} W/L ratio (${wlB}). Previously: ${metadata.kolA} had ${metadata.currentWinsLossesA || '0/0'}, ${metadata.kolB} had ${metadata.currentWinsLossesB || '0/0'}`;

    return { outcome, reason };
  }

  private async resolveTopRankMaintainMarket(metadata: any): Promise<{ outcome: "yes" | "no"; reason: string }> {
    const latestKols = await this.getFreshKolData(20);
    const kolData = latestKols.find(k => k.username === metadata.kolA);

    if (!kolData) {
      console.warn(`Missing KOL data for top rank maintain market`);
      return {
        outcome: "no",
        reason: `Market could not be resolved - missing latest leaderboard data for ${metadata.kolA}`
      };
    }

    const currentRank = kolData.rank;
    const threshold = metadata.threshold || 10;
    const outcome = currentRank <= threshold ? "yes" : "no";
    const reason = `${metadata.kolA} is now ranked #${kolData.rank}. ${outcome === 'yes' ? `Maintained position in top ${threshold}` : `Dropped below top ${threshold}`}. Previously ranked #${metadata.currentRankA}`;

    return { outcome, reason };
  }

  private async resolveStreakContinuationMarket(metadata: any): Promise<{ outcome: "yes" | "no"; reason: string }> {
    const latestKols = await this.getFreshKolData(20);
    const kolData = latestKols.find(k => k.username === metadata.kolA);

    if (!kolData) {
      console.warn(`Missing KOL data for streak continuation market`);
      return {
        outcome: "no",
        reason: `Market could not be resolved - missing latest leaderboard data for ${metadata.kolA}`
      };
    }

    const previousWL = this.parseWinsLosses(metadata.currentWinsLossesA);
    const previousWinRate = this.calculateWinRate(previousWL.wins, previousWL.losses);
    const currentWinRate = this.calculateWinRate(kolData.wins, kolData.losses);

    const outcome = currentWinRate > previousWinRate ? "yes" : "no";
    const currentWL = `${kolData.wins || 0}/${kolData.losses || 0}`;
    const reason = `${metadata.kolA} ${outcome === 'yes' ? 'improved' : 'did not improve'} their win rate. Current: ${currentWL} (${(currentWinRate * 100).toFixed(1)}%), Previous: ${metadata.currentWinsLossesA || '0/0'} (${(previousWinRate * 100).toFixed(1)}%)`;

    return { outcome, reason };
  }

  private async resolveRankImprovementMarket(metadata: any): Promise<{ outcome: "yes" | "no"; reason: string }> {
    const latestKols = await this.getFreshKolData(20);
    const kolData = latestKols.find(k => k.username === metadata.kolA);

    if (!kolData) {
      console.warn(`Missing KOL data for rank improvement market`);
      return {
        outcome: "no",
        reason: `Market could not be resolved - missing latest leaderboard data for ${metadata.kolA}`
      };
    }

    const currentRank = kolData.rank;
    const targetRank = metadata.threshold || 1;
    const outcome = currentRank <= targetRank ? "yes" : "no";
    const reason = `${metadata.kolA} is now ranked #${kolData.rank}. ${outcome === 'yes' ? `Reached target of #${targetRank} or better` : `Did not reach #${targetRank}`}. Previously ranked #${metadata.currentRankA}`;

    return { outcome, reason };
  }

  private async resolveSolGainThresholdMarket(metadata: any): Promise<{ outcome: "yes" | "no"; reason: string }> {
    const latestKols = await this.getFreshKolData(20);
    const kolData = latestKols.find(k => k.username === metadata.kolA);

    if (!kolData || !kolData.solGain) {
      console.warn(`Missing KOL data for SOL gain threshold market`);
      return {
        outcome: "no",
        reason: `Market could not be resolved - missing latest SOL gain data for ${metadata.kolA}`
      };
    }

    const currentSolGain = this.parseSolGain(kolData.solGain);
    const threshold = parseFloat(metadata.threshold) || 50;
    const outcome = currentSolGain >= threshold ? "yes" : "no";
    const reason = `${metadata.kolA} current SOL gain: ${kolData.solGain} (${currentSolGain.toFixed(2)} SOL). ${outcome === 'yes' ? `Reached threshold of +${threshold} SOL` : `Did not reach +${threshold} SOL`}. Previously: ${metadata.currentSolA}`;

    return { outcome, reason };
  }

  private async resolveWinLossRatioMaintainMarket(metadata: any): Promise<{ outcome: "yes" | "no"; reason: string }> {
    const latestKols = await this.getFreshKolData(20);
    const kolData = latestKols.find(k => k.username === metadata.kolA);

    if (!kolData || (kolData.wins === null && kolData.losses === null)) {
      console.warn(`Missing KOL data for win/loss ratio maintain market`);
      return {
        outcome: "no",
        reason: `Market could not be resolved - missing latest W/L data for ${metadata.kolA}`
      };
    }

    const currentRatio = this.calculateWinLossRatio(kolData.wins, kolData.losses);
    const threshold = parseFloat(metadata.threshold) || 1.5;
    const outcome = currentRatio >= threshold ? "yes" : "no";
    const currentWL = `${kolData.wins || 0}/${kolData.losses || 0}`;
    const reason = `${metadata.kolA} current W/L ratio: ${currentRatio.toFixed(2)} (${currentWL}). ${outcome === 'yes' ? `Maintained ratio above ${threshold.toFixed(2)}` : `Did not maintain ${threshold.toFixed(2)}`}. Previously: ${metadata.currentWinsLossesA}`;

    return { outcome, reason };
  }

  private async settleBets(marketId: string, outcome: "yes" | "no"): Promise<number> {
    const allBets = await storage.getMarketBets(marketId);
    const pendingBets = allBets.filter(bet => bet.status === "pending");

    for (const bet of pendingBets) {
      // A bet wins if the shares were purchased (amount > 0) and match the outcome
      // If shares is negative or the position doesn't match outcome, it's a loss
      const shares = parseFloat(bet.shares);
      const won = shares > 0 && bet.position.toLowerCase() === outcome;
      const betAmount = parseFloat(bet.amount);

      let profit: number;
      let newStatus: string;

      if (won) {
        profit = betAmount * 1.5;
        newStatus = "won";
      } else {
        profit = -betAmount;
        newStatus = "lost";
      }

      await storage.updateBetStatus(bet.id, newStatus, profit.toFixed(2));

      const user = await storage.getUser(bet.userId);
      if (user) {
        const currentBalance = parseFloat(user.balance);
        const payout = won ? betAmount + profit : 0;
        const newBalance = (currentBalance + payout).toFixed(2);

        await storage.updateUserBalance(bet.userId, newBalance);

        const newTotalProfit = (parseFloat(user.totalProfit) + profit).toFixed(2);
        const newTotalWins = won ? user.totalWins + 1 : user.totalWins;
        await storage.updateUserStats(bet.userId, user.totalBets, newTotalWins, newTotalProfit);
      }
    }

    return pendingBets.length;
  }

  startAutoResolution(intervalMinutes: number = 5): void {
    if (this.resolutionInterval) {
      console.log("Auto-resolution already running");
      return;
    }

    console.log(`Starting auto-resolution every ${intervalMinutes} minutes`);

    this.resolveExpiredMarkets();

    this.resolutionInterval = setInterval(() => {
      this.resolveExpiredMarkets();
    }, intervalMinutes * 60 * 1000);
  }

  stopAutoResolution(): void {
    if (this.resolutionInterval) {
      clearInterval(this.resolutionInterval);
      this.resolutionInterval = null;
      console.log("Auto-resolution stopped");
    }
  }

  async resolveAllMarkets(): Promise<MarketResolution[]> {
    if (this.isResolving) {
      console.log("Resolution already in progress, skipping...");
      return [];
    }

    this.isResolving = true;
    console.log("Force resolving ALL unresolved markets...");

    let successCount = 0;
    let failureCount = 0;
    const resolutions: MarketResolution[] = [];

    try {
      let markets;
      try {
        markets = await storage.getAllMarketsWithKols();
      } catch (error) {
        console.error("Critical error: Failed to fetch markets from storage:", error);
        return [];
      }

      for (const market of markets) {
        try {
          if (market.isLive && market.outcome === "pending") {
            console.log(`Force resolving market: ${market.title} (${market.id})`);

            const resolution = await this.resolveMarket(market);
            if (resolution) {
              resolutions.push(resolution);
              successCount++;
            } else {
              failureCount++;
            }
          }
        } catch (error) {
          failureCount++;
          console.error(`Error resolving market ${market.id}:`, error);
        }
      }

      console.log(`Force resolution completed: ${successCount} successful, ${failureCount} failed`);
      return resolutions;
    } catch (error) {
      console.error("Unexpected error in force resolution:", error);
      return [];
    } finally {
      this.isResolving = false;
    }
  }
}

export const marketResolver = new MarketResolver();

