import { PublicKey } from "@solana/web3.js";
import { solanaWallet } from "./solana-wallet";
import type { IStorage } from "./storage";

const REQUIRED_CONFIRMATIONS = 1;
const CHECK_INTERVAL_MS = 15000; // Check every 15 seconds

export class SolanaDepositMonitor {
  private storage: IStorage;
  private isMonitoring: boolean = false;
  private monitoringInterval: NodeJS.Timeout | null = null;
  private processedSignatures: Set<string> = new Set();
  private broadcastCallback?: (data: any) => void;

  constructor(storage: IStorage, broadcastCallback?: (data: any) => void) {
    this.storage = storage;
    this.broadcastCallback = broadcastCallback;
  }

  async start() {
    if (this.isMonitoring) {
      console.log("  Deposit monitor already running");
      return;
    }

    this.isMonitoring = true;
    console.log(" Starting Solana deposit monitor...");

    this.monitoringInterval = setInterval(() => {
      this.checkPendingDeposits();
    }, CHECK_INTERVAL_MS);

    await this.checkPendingDeposits();
  }

  stop() {
    if (this.monitoringInterval) {
      clearInterval(this.monitoringInterval);
      this.monitoringInterval = null;
    }
    this.isMonitoring = false;
    console.log(" Solana deposit monitor stopped");
  }

  private async checkPendingDeposits() {
    try {
      const pendingDeposits = await this.storage.getPendingDeposits();

      for (const deposit of pendingDeposits) {
        if (this.processedSignatures.has(deposit.signature)) {
          continue;
        }

        await this.verifyAndProcessDeposit(deposit);
      }
    } catch (error) {
      console.error("Error checking pending deposits:", error);
    }
  }

  private async verifyAndProcessDeposit(deposit: any) {
    try {
      const tx = await solanaWallet.getTransaction(deposit.signature);

      if (!tx) {
        if (this.isDepositExpired(deposit.createdAt)) {
          await this.storage.updateDepositStatus(deposit.id, "failed", 0);
          console.log(` Deposit ${deposit.id} expired (signature not found)`);
        }
        return;
      }

      if (tx.meta?.err) {
        await this.storage.updateDepositStatus(deposit.id, "failed", 0);
        console.log(` Deposit ${deposit.id} failed on-chain`);
        return;
      }

      const confirmations = await solanaWallet.getConfirmations(deposit.signature);

      if (confirmations >= REQUIRED_CONFIRMATIONS) {
        const user = await this.storage.getUser(deposit.userId);
        if (!user) {
          console.error(`User ${deposit.userId} not found for deposit ${deposit.id}`);
          return;
        }

        const newBalance = (parseFloat(user.solanaBalance) + parseFloat(deposit.amount)).toFixed(9);

        await this.storage.updateUserSolanaBalance(deposit.userId, newBalance);
        await this.storage.updateDepositStatus(deposit.id, "confirmed", confirmations);

        this.processedSignatures.add(deposit.signature);

        console.log(` Deposit confirmed: ${deposit.amount} SOL for user ${deposit.userId}`);
        console.log(`   Signature: ${deposit.signature}`);
        console.log(`   New balance: ${newBalance} SOL`);

        // Broadcast deposit confirmation via WebSocket
        if (this.broadcastCallback) {
          this.broadcastCallback({
            type: 'DEPOSIT_CONFIRMED',
            deposit: {
              id: deposit.id,
              userId: deposit.userId,
              amount: deposit.amount,
              signature: deposit.signature,
              status: 'confirmed',
              confirmations
            },
            newBalance
          });
        }
      } else {
        await this.storage.updateDepositStatus(deposit.id, "pending", confirmations);
        console.log(` Deposit ${deposit.id} has ${confirmations}/${REQUIRED_CONFIRMATIONS} confirmations`);
      }
    } catch (error) {
      console.error(`Error processing deposit ${deposit.id}:`, error);
    }
  }

  private isDepositExpired(createdAt: Date): boolean {
    const MAX_AGE_HOURS = 24;
    const age = Date.now() - new Date(createdAt).getTime();
    return age > MAX_AGE_HOURS * 60 * 60 * 1000;
  }

  async recordManualDeposit(userId: string, signature: string, depositAddress: string) {
    try {
      const tx = await solanaWallet.getTransaction(signature);

      if (!tx || tx.meta?.err) {
        throw new Error("Invalid or failed transaction");
      }

      const postBalances = tx.meta?.postBalances || [];
      const preBalances = tx.meta?.preBalances || [];

      if (postBalances.length === 0 || preBalances.length === 0) {
        throw new Error("Unable to determine deposit amount");
      }

      const lamportsReceived = postBalances[1] - preBalances[1];

      if (lamportsReceived <= 0) {
        throw new Error("No SOL received in this transaction");
      }

      const amountSOL = solanaWallet.convertLamportsToSOL(lamportsReceived);

      const deposit = await this.storage.createDeposit({
        userId,
        signature,
        amount: amountSOL.toFixed(9),
        depositAddress,
      });

      console.log(` Manual deposit recorded: ${amountSOL} SOL for user ${userId}`);

      await this.verifyAndProcessDeposit(deposit);

      return deposit;
    } catch (error: any) {
      console.error("Error recording manual deposit:", error);
      throw new Error(`Failed to record deposit: ${error.message}`);
    }
  }
}

export function createDepositMonitor(storage: IStorage, broadcastCallback?: (data: any) => void): SolanaDepositMonitor {
  return new SolanaDepositMonitor(storage, broadcastCallback);
}

server/solana-withdrawal-processor.ts.content:
import { solanaWallet } from "./solana-wallet";
import type { IStorage } from "./storage";

const DAILY_WITHDRAWAL_LIMIT_SOL = parseFloat(process.env.DAILY_WITHDRAWAL_LIMIT || "100");
const MIN_WITHDRAWAL_AMOUNT = 0.001; // 0.001 SOL minimum
const MAX_WITHDRAWAL_AMOUNT = 10; // 10 SOL maximum per withdrawal
const PROCESS_INTERVAL_MS = 30000; // Process every 30 seconds

export class SolanaWithdrawalProcessor {
  private storage: IStorage;
  private isProcessing: boolean = false;
  private processingInterval: NodeJS.Timeout | null = null;
  private broadcastCallback?: (data: any) => void;

  constructor(storage: IStorage, broadcastCallback?: (data: any) => void) {
    this.storage = storage;
    this.broadcastCallback = broadcastCallback;
  }

  async start() {
    if (this.isProcessing) {
      console.log("  Withdrawal processor already running");
      return;
    }

    this.isProcessing = true;
    console.log(" Starting Solana withdrawal processor...");

    this.processingInterval = setInterval(() => {
      this.processPendingWithdrawals();
    }, PROCESS_INTERVAL_MS);

    await this.processPendingWithdrawals();
  }

  stop() {
    if (this.processingInterval) {
      clearInterval(this.processingInterval);
      this.processingInterval = null;
    }
    this.isProcessing = false;
    console.log(" Solana withdrawal processor stopped");
  }

  private async processPendingWithdrawals() {
    try {
      const pendingWithdrawals = await this.storage.getPendingWithdrawals();

      for (const withdrawal of pendingWithdrawals) {
        await this.processWithdrawal(withdrawal);
      }
    } catch (error) {
      console.error("Error processing pending withdrawals:", error);
    }
  }

  private async processWithdrawal(withdrawal: any) {
    try {
      const user = await this.storage.getUser(withdrawal.userId);
      if (!user) {
        await this.storage.updateWithdrawalStatus(
          withdrawal.id,
          "failed",
          undefined,
          "User not found"
        );
        return;
      }

      const amount = parseFloat(withdrawal.amount);
      const userBalance = parseFloat(user.solanaBalance);

      if (userBalance < amount) {
        await this.storage.updateWithdrawalStatus(
          withdrawal.id,
          "failed",
          undefined,
          "Insufficient balance"
        );
        console.log(` Withdrawal ${withdrawal.id} failed: Insufficient balance`);
        return;
      }

      console.log(` Processing withdrawal: ${amount} SOL to ${withdrawal.destinationAddress}`);
      console.log(`   User balance: ${userBalance} SOL`);

      const signature = await solanaWallet.transferSOL(
        withdrawal.destinationAddress,
        amount
      );

      const newBalance = (userBalance - amount).toFixed(9);
      await this.storage.updateUserSolanaBalance(withdrawal.userId, newBalance);

      await this.storage.updateWithdrawalStatus(
        withdrawal.id,
        "completed",
        signature,
        undefined
      );

      console.log(` Withdrawal completed: ${amount} SOL`);
      console.log(`   Signature: ${signature}`);
      console.log(`   New user balance: ${newBalance} SOL`);

      // Broadcast withdrawal completion via WebSocket
      if (this.broadcastCallback) {
        this.broadcastCallback({
          type: 'WITHDRAWAL_COMPLETED',
          withdrawal: {
            id: withdrawal.id,
            userId: withdrawal.userId,
            amount: withdrawal.amount,
            destinationAddress: withdrawal.destinationAddress,
            signature,
            status: 'completed'
          },
          newBalance
        });
      }
    } catch (error: any) {
      console.error(` Error processing withdrawal ${withdrawal.id}:`, error);

      await this.storage.updateWithdrawalStatus(
        withdrawal.id,
        "failed",
        undefined,
        error.message || "Unknown error"
      );

      // Broadcast withdrawal failure via WebSocket
      if (this.broadcastCallback) {
        this.broadcastCallback({
          type: 'WITHDRAWAL_FAILED',
          withdrawal: {
            id: withdrawal.id,
            userId: withdrawal.userId,
            amount: withdrawal.amount,
            destinationAddress: withdrawal.destinationAddress,
            status: 'failed',
            error: error.message || "Unknown error"
          }
        });
      }
    }
  }

  async requestWithdrawal(userId: string, destinationAddress: string, amount: number): Promise<any> {
    if (!solanaWallet.isValidAddress(destinationAddress)) {
      throw new Error("Invalid Solana address");
    }

    if (amount < MIN_WITHDRAWAL_AMOUNT) {
      throw new Error(`Minimum withdrawal amount is ${MIN_WITHDRAWAL_AMOUNT} SOL`);
    }

    if (amount > MAX_WITHDRAWAL_AMOUNT) {
      throw new Error(`Maximum withdrawal amount is ${MAX_WITHDRAWAL_AMOUNT} SOL per transaction`);
    }

    const user = await this.storage.getUser(userId);
    if (!user) {
      throw new Error("User not found");
    }

    const userBalance = parseFloat(user.solanaBalance);
    if (userBalance < amount) {
      throw new Error(`Insufficient balance. Available: ${userBalance} SOL`);
    }

    const today = new Date();
    today.setHours(0, 0, 0, 0);

    const recentWithdrawals = await this.storage.getUserWithdrawals(userId, 100);
    const todayWithdrawals = recentWithdrawals.filter(w => {
      const wDate = new Date(w.createdAt);
      wDate.setHours(0, 0, 0, 0);
      return wDate.getTime() === today.getTime() && w.status !== "failed";
    });

    const todayTotal = todayWithdrawals.reduce(
      (sum, w) => sum + parseFloat(w.amount),
      0
    );

    if (todayTotal + amount > DAILY_WITHDRAWAL_LIMIT_SOL) {
      throw new Error(
        `Daily withdrawal limit exceeded. Limit: ${DAILY_WITHDRAWAL_LIMIT_SOL} SOL, ` +
        `Used today: ${todayTotal.toFixed(3)} SOL, ` +
        `Remaining: ${(DAILY_WITHDRAWAL_LIMIT_SOL - todayTotal).toFixed(3)} SOL`
      );
    }

    const withdrawal = await this.storage.createWithdrawal({
      userId,
      destinationAddress,
      amount: amount.toFixed(9),
    });

    console.log(` Withdrawal request created: ${amount} SOL for user ${userId}`);
    console.log(`   Destination: ${destinationAddress}`);
    console.log(`   Request ID: ${withdrawal.id}`);

    setImmediate(() => {
      this.processWithdrawal(withdrawal);
    });

    return withdrawal;
  }

  async getWithdrawalLimits(userId: string) {
    const today = new Date();
    today.setHours(0, 0, 0, 0);

    const recentWithdrawals = await this.storage.getUserWithdrawals(userId, 100);
    const todayWithdrawals = recentWithdrawals.filter(w => {
      const wDate = new Date(w.createdAt);
      wDate.setHours(0, 0, 0, 0);
      return wDate.getTime() === today.getTime() && w.status !== "failed";
    });

    const todayTotal = todayWithdrawals.reduce(
      (sum, w) => sum + parseFloat(w.amount),
      0
    );

    return {
      dailyLimit: DAILY_WITHDRAWAL_LIMIT_SOL,
      usedToday: todayTotal,
      remainingToday: DAILY_WITHDRAWAL_LIMIT_SOL - todayTotal,
      minWithdrawal: MIN_WITHDRAWAL_AMOUNT,
      maxWithdrawal: MAX_WITHDRAWAL_AMOUNT,
    };
  }
}

export function createWithdrawalProcessor(storage: IStorage, broadcastCallback?: (data: any) => void): SolanaWithdrawalProcessor {
  return new SolanaWithdrawalProcessor(storage, broadcastCallback);
}

