              <div>
                <p className="text-sm text-muted-foreground font-medium mb-1">ROI</p>
                <p className={`text-2xl font-bold tabular-nums ${roi >= 0 ? 'text-success' : 'text-destructive'}`} data-testid="text-roi">
                  {roi >= 0 ? '+' : ''}{roi.toFixed(1)}%
                </p>
              </div>
            </div>
