import { useState, useEffect } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Navbar } from "@/components/navbar";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Avatar, AvatarImage, AvatarFallback } from "@/components/ui/avatar";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogDescription } from "@/components/ui/dialog";
import { useToast } from "@/hooks/use-toast";
import { useWebSocket } from "@/hooks/use-websocket";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { Send, MessageCircle, Plus, Search, Loader2, Trash2 } from "lucide-react";
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
} from "@/components/ui/alert-dialog";
import type { User, ConversationWithParticipants, Message } from "@shared/schema";
import { format } from "date-fns";

export default function Messages() {
  const [selectedConversationId, setSelectedConversationId] = useState<string | null>(null);
  const [messageInput, setMessageInput] = useState("");
  const [newConversationOpen, setNewConversationOpen] = useState(false);
  const [userSearch, setUserSearch] = useState("");
  const [deleteDialogOpen, setDeleteDialogOpen] = useState(false);
  const [conversationToDelete, setConversationToDelete] = useState<string | null>(null);
  const { toast } = useToast();

  useWebSocket();

  const { data: user } = useQuery<User>({
    queryKey: ["/api/user"],
  });

  const { data: conversations = [], isLoading: conversationsLoading } = useQuery<ConversationWithParticipants[]>({
    queryKey: ["/api/conversations"],
    queryFn: async () => {
      const result = await apiRequest("GET", "/api/conversations");
      return result.json();
    },
    enabled: !!user,
  });

  const { data: messages = [] } = useQuery<Message[]>({
    queryKey: ["/api/conversations", selectedConversationId, "messages"],
    enabled: !!selectedConversationId,
  });

  const { data: allUsers = [] } = useQuery<User[]>({
    queryKey: ["/api/users"],
    enabled: newConversationOpen,
  });

  const filteredUsers = allUsers.filter((u) => {
    if (!u.username) return false; // Skip users without usernames
    if (u.id === user?.id) return false; // Skip current user
    if (!userSearch.trim()) return true;
    const searchLower = userSearch.toLowerCase();
    return u.username.toLowerCase().includes(searchLower);
  }).slice(0, 20);

  const createConversationMutation = useMutation({
    mutationFn: async (otherUserId: string) => {
      const result = await apiRequest("POST", "/api/conversations", {
        otherUserId,
      });
      return result.json();
    },
    onSuccess: (conversation) => {
      queryClient.invalidateQueries({ queryKey: ["/api/conversations"] });
      setSelectedConversationId(conversation.id);
      setNewConversationOpen(false);
      setUserSearch("");
      toast({
        title: "Success",
        description: "Conversation created",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to create conversation",
        variant: "destructive",
      });
    },
  });

  const sendMessageMutation = useMutation({
    mutationFn: async (content: string) => {
      if (!selectedConversationId) return;
      return await apiRequest("POST", `/api/conversations/${selectedConversationId}/messages`, {
        content,
      });
    },
    onSuccess: () => {
      setMessageInput("");
      queryClient.invalidateQueries({ queryKey: ["/api/conversations", selectedConversationId, "messages"] });
      queryClient.invalidateQueries({ queryKey: ["/api/conversations"] });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to send message",
        variant: "destructive",
      });
    },
  });

  const markAsReadMutation = useMutation({
    mutationFn: async (conversationId: string) => {
      return await apiRequest("PUT", `/api/conversations/${conversationId}/read`, {});
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/conversations"] });
    },
  });

  const deleteConversationMutation = useMutation({
    mutationFn: async (conversationId: string) => {
      return await apiRequest("DELETE", `/api/conversations/${conversationId}`, {});
    },
    onSuccess: (_, deletedId) => {
      queryClient.invalidateQueries({ queryKey: ["/api/conversations"] });
      // Clear selection if we deleted the currently selected conversation
      if (selectedConversationId === deletedId) {
        setSelectedConversationId(null);
      }
      setDeleteDialogOpen(false);
      setConversationToDelete(null);
      toast({
        title: "Conversation deleted",
        description: "The conversation has been removed",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to delete conversation",
        variant: "destructive",
      });
    },
  });

  const handleSendMessage = () => {
    if (!messageInput.trim()) return;
    sendMessageMutation.mutate(messageInput.trim());
  };

  const handleSelectConversation = (conversationId: string) => {
    setSelectedConversationId(conversationId);
    markAsReadMutation.mutate(conversationId);
  };

  const handleCreateConversation = (otherUserId: string) => {
    if (!user) {
      toast({
        title: "Authentication Required",
        description: "Please log in to start a conversation",
        variant: "destructive",
      });
      return;
    }
    createConversationMutation.mutate(otherUserId);
  };

  const selectedConversation = conversations.find(c => c.id === selectedConversationId);
  const otherUser = selectedConversation
    ? (selectedConversation.user1Id === user?.id ? selectedConversation.user2 : selectedConversation.user1)
    : null;

  return (
    <div className="min-h-screen bg-background">
      <Navbar balance={user?.balance ? parseFloat(user.balance) : 1000} username={user?.username ?? undefined} />

      <div className="container mx-auto px-4 py-10">
        <div className="mb-8">
          <h1 className="text-3xl font-display font-bold mb-2" data-testid="text-page-title">Messages</h1>
          <p className="text-muted-foreground">Connect with other traders</p>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 h-[calc(100vh-16rem)]">
          {/* Conversations List */}
          <Card className="lg:col-span-1 overflow-hidden border-border/60 flex flex-col">
            <div className="p-5 border-b border-border/50 flex items-center justify-between gap-2">
              <h2 className="font-semibold">Conversations</h2>
              <Button
                size="icon"
                variant="ghost"
                onClick={() => setNewConversationOpen(true)}
                data-testid="button-new-conversation"
              >
                <Plus className="h-5 w-5" />
              </Button>
            </div>

            {conversationsLoading ? (
              <div className="flex-1 flex items-center justify-center">
                <div className="animate-pulse text-muted-foreground">Loading...</div>
              </div>
            ) : conversations.length === 0 ? (
              <div className="flex-1 flex flex-col items-center justify-center p-8 text-center text-muted-foreground">
                <MessageCircle className="h-12 w-12 mb-4 opacity-50" />
                <p className="font-medium mb-1">No conversations yet</p>
                <p className="text-sm mb-4">Start chatting with other traders</p>
                <Button
                  onClick={() => setNewConversationOpen(true)}
                  data-testid="button-start-conversation"
                >
                  <Plus className="h-4 w-4 mr-2" />
                  New Conversation
                </Button>
              </div>
            ) : (
              <ScrollArea className="flex-1">
                <div className="p-2 space-y-1">
                  {conversations.map((conversation) => {
                    const other = conversation.user1Id === user?.id ? conversation.user2 : conversation.user1;
                    const hasUnread = conversation.unreadCount > 0;

                    // Skip conversation if other user data is missing
                    if (!other || !other.username) {
                      return null;
                    }

                    return (
                      <div
                        key={conversation.id}
                        onClick={() => handleSelectConversation(conversation.id)}
                        className={`p-4 rounded-lg cursor-pointer transition-all hover-elevate ${
                          selectedConversationId === conversation.id
                            ? "bg-primary/10 border-primary/20"
                            : "border-transparent"
                        } border`}
                        data-testid={`conversation-${conversation.id}`}
                      >
                        <div className="flex items-center gap-3">
                          <Avatar className="h-12 w-12 ring-2 ring-border">
                            <AvatarImage src={other.avatarUrl ?? undefined} alt={other.username} />
                            <AvatarFallback>{other.username[0]?.toUpperCase() ?? "U"}</AvatarFallback>
                          </Avatar>
                          <div className="flex-1 min-w-0">
                            <div className="flex items-center justify-between gap-2">
                              <p className={`font-medium text-sm truncate ${hasUnread && "font-bold"}`}>
                                {other.username}
                              </p>
                              {hasUnread && (
                                <Badge className="bg-primary">
                                  {conversation.unreadCount}
                                </Badge>
                              )}
                            </div>
                            <p className="text-xs text-muted-foreground truncate">
                              {format(new Date(conversation.lastMessageAt), "MMM d, h:mm a")}
                            </p>
                          </div>
                        </div>
                      </div>
                    );
                  })}
                </div>
              </ScrollArea>
            )}
          </Card>

          {/* Chat Interface */}
          <Card className="lg:col-span-2 overflow-hidden border-border/60 flex flex-col">
            {!selectedConversationId ? (
              <div className="flex-1 flex flex-col items-center justify-center p-8 text-center text-muted-foreground">
                <MessageCircle className="h-16 w-16 mb-4 opacity-50" />
                <p className="font-medium mb-1">Select a conversation</p>
                <p className="text-sm">Choose a conversation from the list to start messaging</p>
              </div>
            ) : (
              <>
                {/* Chat Header */}
                <div className="p-5 border-b border-border/50 flex items-center justify-between gap-3">
                  <div className="flex items-center gap-3">
                    <Avatar className="h-10 w-10 ring-2 ring-border">
                      <AvatarImage src={otherUser?.avatarUrl ?? undefined} alt={otherUser?.username ?? "User"} />
                      <AvatarFallback>{otherUser?.username?.[0]?.toUpperCase() ?? "U"}</AvatarFallback>
                    </Avatar>
                    <div>
                      <h2 className="font-semibold">{otherUser?.username ?? "Unknown User"}</h2>
                      <p className="text-xs text-muted-foreground">Online</p>
                    </div>
                  </div>
                  <Button 
                    size="icon" 
                    variant="ghost" 
                    className="text-destructive"
                    onClick={() => {
                      setConversationToDelete(selectedConversationId);
                      setDeleteDialogOpen(true);
                    }}
                  >
                    <Trash2 className="h-4 w-4" />
                  </Button>
                </div>

                {/* Messages */}
                <ScrollArea className="flex-1 p-5">
                  {messages.length === 0 ? (
                    <div className="flex items-center justify-center h-full text-muted-foreground" data-testid="text-no-messages">
                      <p className="text-sm">No messages yet. Say hello!</p>
                    </div>
                  ) : (
                    <div className="space-y-4">
                      {messages.map((message) => {
                        const isOwnMessage = message.senderId === user?.id;

                        return (
                          <div
                            key={message.id}
                            className={`flex ${isOwnMessage ? "justify-end" : "justify-start"}`}
                            data-testid={`message-${message.id}`}
                          >
                            <div
                              className={`max-w-[70%] p-3 rounded-lg ${
                                isOwnMessage
                                  ? "bg-primary text-primary-foreground"
                                  : "bg-muted"
                              }`}
                            >
                              <p className="text-sm">{message.content}</p>
                              <p className={`text-xs mt-1 ${isOwnMessage ? "text-primary-foreground/70" : "text-muted-foreground"}`}>
                                {format(new Date(message.createdAt), "h:mm a")}
                              </p>
                            </div>
                          </div>
                        );
                      })}
                    </div>
                  )}
                </ScrollArea>

                {/* Message Input */}
                <div className="p-5 border-t border-border/50">
                  <div className="flex items-center gap-2">
                    <Input
                      placeholder="Type a message..."
                      value={messageInput}
                      onChange={(e) => setMessageInput(e.target.value)}
                      onKeyDown={(e) => {
                        if (e.key === "Enter" && !e.shiftKey) {
                          e.preventDefault();
                          handleSendMessage();
                        }
                      }}
                      className="flex-1"
                      disabled={sendMessageMutation.isPending}
                      data-testid="input-message"
                    />
                    <Button
                      onClick={handleSendMessage}
                      disabled={!messageInput.trim() || sendMessageMutation.isPending}
                      className="gap-2"
                      data-testid="button-send"
                    >
                      {sendMessageMutation.isPending ? (
                        <Loader2 className="h-4 w-4 animate-spin" />
                      ) : (
                        <Send className="h-4 w-4" />
                      )}
                      Send
                    </Button>
                  </div>
                </div>
              </>
            )}
          </Card>
        </div>
      </div>

      {/* Delete Conversation Dialog */}
      <AlertDialog open={deleteDialogOpen} onOpenChange={setDeleteDialogOpen}>
        <AlertDialogContent>
          <AlertDialogHeader>
            <AlertDialogTitle>Delete Conversation</AlertDialogTitle>
            <AlertDialogDescription>
              Are you sure you want to delete this conversation? This will permanently remove all messages and cannot be undone.
            </AlertDialogDescription>
          </AlertDialogHeader>
          <AlertDialogFooter>
            <AlertDialogCancel>Cancel</AlertDialogCancel>
            <AlertDialogAction
              onClick={() => {
                if (conversationToDelete) {
                  deleteConversationMutation.mutate(conversationToDelete);
                }
              }}
              className="bg-destructive text-destructive-foreground hover:bg-destructive/90"
            >
              Delete
            </AlertDialogAction>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>

      {/* New Conversation Dialog */}
      <Dialog open={newConversationOpen} onOpenChange={setNewConversationOpen}>
        <DialogContent data-testid="dialog-new-conversation">
          <DialogHeader>
            <DialogTitle>New Conversation</DialogTitle>
            <DialogDescription>
              Search for a user to start a conversation
            </DialogDescription>
          </DialogHeader>

          <div className="space-y-4">
            <div className="relative">
              <Search className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 text-muted-foreground" />
              <Input
                placeholder="Search users..."
                value={userSearch}
                onChange={(e) => setUserSearch(e.target.value)}
                className="pl-10"
                data-testid="input-search-users"
              />
            </div>

            <ScrollArea className="h-[300px] border rounded-lg">
              {filteredUsers.length === 0 ? (
                <div className="flex items-center justify-center h-full p-8 text-center text-muted-foreground">
                  <p className="text-sm">
                    {userSearch.trim() ? "No users found" : "Start typing to search users"}
                  </p>
                </div>
              ) : (
                <div className="p-2 space-y-1">
                  {filteredUsers.map((searchUser) => (
                    <div
                      key={searchUser.id}
                      onClick={() => handleCreateConversation(searchUser.id)}
                      className="p-3 rounded-lg cursor-pointer transition-all hover-elevate border border-transparent"
                      data-testid={`user-search-result-${searchUser.id}`}
                    >
                      <div className="flex items-center gap-3">
                        <Avatar className="h-10 w-10 ring-2 ring-border">
                          <AvatarImage src={undefined} alt={searchUser.username ?? 'User'} />
                          <AvatarFallback>{searchUser.username?.[0]?.toUpperCase() ?? 'U'}</AvatarFallback>
                        </Avatar>
                        <div className="flex-1 min-w-0">
                          <p className="font-medium text-sm truncate">{searchUser.username ?? 'Unknown User'}</p>
                          <p className="text-xs text-muted-foreground">
                            {searchUser.totalBets} bets  {searchUser.totalWins} wins
                          </p>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </ScrollArea>

            {createConversationMutation.isPending && (
              <div className="flex items-center justify-center gap-2 text-sm text-muted-foreground">
                <Loader2 className="h-4 w-4 animate-spin" />
                Creating conversation...
              </div>
            )}
          </div>
        </DialogContent>
      </Dialog>
    </div>
  );
}

client/src/pages/help.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { Navbar } from "@/components/navbar";
import { Card } from "@/components/ui/card";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import {
  Accordion,
  AccordionContent,
  AccordionItem,
  AccordionTrigger,
} from "@/components/ui/accordion";
import { HelpCircle } from "lucide-react";
import type { User, Faq } from "@shared/schema";

export default function Help() {
  const { data: user } = useQuery<User>({
    queryKey: ["/api/user"],
  });

  const { data: faqs = [], isLoading } = useQuery<Faq[]>({
    queryKey: ["/api/faqs"],
  });

  const categories = Array.from(new Set(faqs.map(faq => faq.category)));
  const categorizedFaqs = (category: string) => 
    faqs.filter(faq => faq.category === category).sort((a, b) => a.order - b.order);

  return (
    <div className="min-h-screen bg-background">
      <Navbar balance={user?.balance ? parseFloat(user.balance) : 1000} username={user?.username ?? undefined} />

      <div className="container mx-auto px-4 py-10 max-w-4xl">
        <div className="mb-10 text-center">
          <div className="inline-flex items-center justify-center w-16 h-16 rounded-full bg-primary/10 ring-4 ring-primary/10 mb-6">
            <HelpCircle className="h-8 w-8 text-primary" />
          </div>
          <h1 className="text-4xl font-display font-bold mb-3" data-testid="text-page-title">
            Help Center
          </h1>
          <p className="text-lg text-muted-foreground max-w-2xl mx-auto">
            Find answers to common questions about KOL Market
          </p>
        </div>

        {isLoading ? (
          <Card className="p-8">
            <div className="animate-pulse text-center text-muted-foreground">Loading FAQs...</div>
          </Card>
        ) : faqs.length === 0 ? (
          <Card className="p-16">
            <div className="text-center text-muted-foreground">
              <HelpCircle className="h-12 w-12 mx-auto mb-4 opacity-50" />
              <p className="font-medium mb-1">No FAQs available</p>
              <p className="text-sm">FAQs will be added soon</p>
            </div>
          </Card>
        ) : (
          <Card>
            <Tabs defaultValue={categories[0] || "all"} className="p-6">
              <TabsList className="grid w-full grid-cols-4 mb-8">
                {categories.map((cat) => (
                  <TabsTrigger 
                    key={cat} 
                    value={cat} 
                    className="capitalize"
                    data-testid={`tab-${cat}`}
                  >
                    {cat.replace(/_/g, " ")}
                  </TabsTrigger>
                ))}
              </TabsList>

              {categories.map((cat) => (
                <TabsContent key={cat} value={cat}>
                  <Accordion type="single" collapsible className="space-y-2">
                    {categorizedFaqs(cat).map((faq, index) => (
                      <AccordionItem 
                        key={faq.id} 
                        value={faq.id}
                        className="border border-border/60 rounded-lg px-6 data-[state=open]:bg-muted/30"
                        data-testid={`faq-${faq.id}`}
                      >
                        <AccordionTrigger className="hover:no-underline py-5">
                          <div className="flex items-start gap-4 text-left">
                            <div className="flex items-center justify-center w-8 h-8 rounded-full bg-primary/10 text-primary text-sm font-bold flex-shrink-0 mt-0.5">
                              {index + 1}
                            </div>
                            <span className="font-semibold">{faq.question}</span>
                          </div>
                        </AccordionTrigger>
                        <AccordionContent className="pl-12 pr-4 pb-5 text-muted-foreground">
                          {faq.answer}
                        </AccordionContent>
                      </AccordionItem>
                    ))}
                  </Accordion>
                </TabsContent>
              ))}
            </Tabs>
          </Card>
        )}

        <Card className="mt-10 p-8 bg-muted/30">
          <div className="text-center">
            <h2 className="text-xl font-semibold mb-2">Still need help?</h2>
            <p className="text-muted-foreground mb-4">
              Can't find what you're looking for? Our support team is here to help.
            </p>
            <p className="text-sm text-muted-foreground">
              Join our community forum or reach out to us directly for personalized assistance.
            </p>
          </div>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/forum.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Navbar } from "@/components/navbar";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Avatar, AvatarImage, AvatarFallback } from "@/components/ui/avatar";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import { ScrollArea } from "@/components/ui/scroll-area";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { Plus, ThumbsUp, ThumbsDown, MessageSquare, TrendingUp } from "lucide-react";
import type { User, ForumThread, ForumComment } from "@shared/schema";
import { format } from "date-fns";

export default function Forum() {
  const [category, setCategory] = useState("all");
  const [newThreadOpen, setNewThreadOpen] = useState(false);
  const [newThreadTitle, setNewThreadTitle] = useState("");
  const [newThreadContent, setNewThreadContent] = useState("");
  const [newThreadCategory, setNewThreadCategory] = useState("general");
  const [selectedThreadId, setSelectedThreadId] = useState<string | null>(null);
  const [newCommentContent, setNewCommentContent] = useState("");
  const { toast } = useToast();

  const { data: user } = useQuery<User>({
    queryKey: ["/api/user"],
  });

  const threadsUrl = category === "all"
    ? "/api/forum/threads"
    : `/api/forum/threads?category=${category}`;

  const { data: threads = [], isLoading: threadsLoading } = useQuery<(ForumThread & { user: { username: string | null } })[]>({
    queryKey: ["/api/forum/threads", category],
    queryFn: async () => {
      const res = await fetch(threadsUrl, { credentials: "include" });
      if (!res.ok) throw new Error("Failed to fetch threads");
      return res.json();
    },
  });

  const { data: comments = [] } = useQuery<(ForumComment & { user: { username: string | null } })[]>({
    queryKey: ["/api/forum/threads", selectedThreadId, "comments"],
    queryFn: async () => {
      if (!selectedThreadId) throw new Error("No thread selected");
      const res = await fetch(`/api/forum/threads/${selectedThreadId}/comments`, { credentials: "include" });
      if (!res.ok) throw new Error("Failed to fetch comments");
      return res.json();
    },
    enabled: !!selectedThreadId,
  });

  const createThreadMutation = useMutation({
    mutationFn: async () => {
      if (!user) throw new Error("Authentication required");
      return await apiRequest("POST", "/api/forum/threads", {
        title: newThreadTitle,
        content: newThreadContent,
        category: newThreadCategory,
      });
    },
    onSuccess: () => {
      setNewThreadOpen(false);
      setNewThreadTitle("");
      setNewThreadContent("");
      queryClient.invalidateQueries({ queryKey: ["/api/forum/threads"] });
      toast({
        title: "Thread created",
        description: "Your thread has been posted successfully",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to create thread",
        variant: "destructive",
      });
    },
  });

  const createCommentMutation = useMutation({
    mutationFn: async ({ threadId, content }: { threadId: string; content: string }) => {
      if (!user) {
        toast({
          title: "Authentication required",
          description: "Please log in to comment on threads",
          variant: "destructive",
        });
        throw new Error("Authentication required");
      }
      return await apiRequest("POST", `/api/forum/threads/${threadId}/comments`, {
        content,
      });
    },
    onSuccess: (_, variables) => {
      setNewCommentContent("");
      queryClient.invalidateQueries({ queryKey: ["/api/forum/threads", variables.threadId, "comments"] });
      queryClient.invalidateQueries({ queryKey: ["/api/forum/threads"] });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to post comment",
        variant: "destructive",
      });
    },
  });

  const voteThreadMutation = useMutation({
    mutationFn: async ({ threadId, vote }: { threadId: string; vote: "up" | "down" }) => {
      if (!user) {
        toast({
          title: "Authentication required",
          description: "Please log in to vote on threads",
          variant: "destructive",
        });
        throw new Error("Authentication required");
      }
      return await apiRequest("POST", `/api/forum/threads/${threadId}/vote`, {
        vote,
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/forum/threads"] });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to vote",
        variant: "destructive",
      });
    },
  });

  const selectedThread = threads.find(t => t.id === selectedThreadId);
  const filteredThreads = category === "all" ? threads : threads.filter(t => t.category === category);

  return (
    <div className="min-h-screen bg-background">
      <Navbar balance={user?.balance ? parseFloat(user.balance) : 1000} username={user?.username ?? undefined} />

      <div className="container mx-auto px-4 py-10">
        <div className="flex items-center justify-between mb-8">
          <div>
            <h1 className="text-3xl font-display font-bold mb-2" data-testid="text-page-title">Forum</h1>
            <p className="text-muted-foreground">Discuss strategies, markets, and KOLs</p>
          </div>

          <Dialog open={newThreadOpen} onOpenChange={setNewThreadOpen}>
            <DialogTrigger asChild>
              <Button className="gap-2" data-testid="button-new-thread" disabled={!user}>
                <Plus className="h-4 w-4" />
                New Thread
              </Button>
            </DialogTrigger>
            <DialogContent>
              <DialogHeader>
                <DialogTitle>Create New Thread</DialogTitle>
                <DialogDescription>
                  {!user ? "Please log in to create a thread" : "Start a discussion with the community"}
                </DialogDescription>
              </DialogHeader>
              {!user ? (
                <div className="p-8 text-center text-muted-foreground">
                  <p className="mb-4">You need to be logged in to create threads</p>
                  <Button onClick={() => setNewThreadOpen(false)}>Close</Button>
                </div>
              ) : (
                <div className="space-y-4 mt-4">
                  <div>
                    <label className="text-sm font-medium mb-2 block">Title</label>
                    <Input
                      placeholder="Thread title..."
                      value={newThreadTitle}
                      onChange={(e) => setNewThreadTitle(e.target.value)}
                      data-testid="input-thread-title"
                    />
                  </div>
                  <div>
                    <label className="text-sm font-medium mb-2 block">Category</label>
                    <select
                      value={newThreadCategory}
                      onChange={(e) => setNewThreadCategory(e.target.value)}
                      className="w-full p-2 rounded-md border border-border bg-background"
                      data-testid="select-category"
                    >
                      <option value="general">General</option>
                      <option value="strategies">Strategies</option>
                      <option value="kols">KOLs</option>
                      <option value="markets">Markets</option>
                    </select>
                  </div>
                  <div>
                    <label className="text-sm font-medium mb-2 block">Content</label>
                    <Textarea
                      placeholder="Share your thoughts..."
                      value={newThreadContent}
                      onChange={(e) => setNewThreadContent(e.target.value)}
                      rows={6}
                      data-testid="textarea-thread-content"
                    />
                  </div>
                  <Button
                    onClick={() => createThreadMutation.mutate()}
                    disabled={!newThreadTitle.trim() || !newThreadContent.trim() || createThreadMutation.isPending}
                    className="w-full"
                    data-testid="button-post-thread"
                  >
                    {createThreadMutation.isPending ? "Posting..." : "Post Thread"}
                  </Button>
                </div>
              )}
            </DialogContent>
          </Dialog>
        </div>

        {/* Category Tabs */}
        <Tabs value={category} onValueChange={setCategory} className="mb-6">
          <TabsList>
            <TabsTrigger value="all" data-testid="tab-all">All</TabsTrigger>
            <TabsTrigger value="general" data-testid="tab-general">General</TabsTrigger>
            <TabsTrigger value="strategies" data-testid="tab-strategies">Strategies</TabsTrigger>
            <TabsTrigger value="kols" data-testid="tab-kols">KOLs</TabsTrigger>
            <TabsTrigger value="markets" data-testid="tab-markets">Markets</TabsTrigger>
          </TabsList>

          <TabsContent value={category} className="mt-6">
            <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
              {/* Thread List */}
              <div className="lg:col-span-2 space-y-3">
                {threadsLoading ? (
                  <Card className="p-8">
                    <div className="animate-pulse text-center text-muted-foreground">Loading threads...</div>
                  </Card>
                ) : filteredThreads.length === 0 ? (
                  <Card className="p-8">
                    <div className="text-center text-muted-foreground">
                      <MessageSquare className="h-12 w-12 mx-auto mb-4 opacity-50" />
                      <p className="font-medium mb-1">No threads yet</p>
                      <p className="text-sm">Be the first to start a discussion!</p>
                    </div>
                  </Card>
                ) : (
                  filteredThreads.map((thread) => (
                    <Card
                      key={thread.id}
                      className={`p-5 hover-elevate transition-all cursor-pointer ${
                        selectedThreadId === thread.id ? "ring-2 ring-primary" : ""
                      }`}
                      onClick={() => setSelectedThreadId(thread.id)}
                      data-testid={`thread-${thread.id}`}
                    >
                      <div className="flex items-start gap-4">
                        {/* Voting */}
                        <div className="flex flex-col items-center gap-1">
                          <Button
                            size="icon"
                            variant="ghost"
                            className="h-8 w-8"
                            onClick={(e) => {
                              e.stopPropagation();
                              voteThreadMutation.mutate({ threadId: thread.id, vote: "up" });
                            }}
                            disabled={!user || voteThreadMutation.isPending}
                            data-testid={`button-upvote-${thread.id}`}
                          >
                            <ThumbsUp className="h-4 w-4" />
                          </Button>
                          <span className="text-sm font-bold tabular-nums">
                            {thread.upvotes - thread.downvotes}
                          </span>
                          <Button
                            size="icon"
                            variant="ghost"
                            className="h-8 w-8"
                            onClick={(e) => {
                              e.stopPropagation();
                              voteThreadMutation.mutate({ threadId: thread.id, vote: "down" });
                            }}
                            disabled={!user || voteThreadMutation.isPending}
                            data-testid={`button-downvote-${thread.id}`}
                          >
                            <ThumbsDown className="h-4 w-4" />
                          </Button>
                        </div>

                        {/* Thread Content */}
                        <div className="flex-1 min-w-0">
                          <div className="flex items-start justify-between gap-2 mb-2">
                            <h3 className="font-semibold text-lg">{thread.title}</h3>
                            <Badge variant="secondary" className="capitalize">
                              {thread.category}
                            </Badge>
                          </div>
                          <p className="text-sm text-muted-foreground line-clamp-2 mb-3">
                            {thread.content}
                          </p>
                          <div className="flex items-center gap-4 text-xs text-muted-foreground">
                            <div className="flex items-center gap-2">
                              <Avatar className="h-5 w-5">
                                <AvatarFallback className="text-xs">
                                  {thread.user?.username?.[0]?.toUpperCase() ?? "U"}
                                </AvatarFallback>
                              </Avatar>
                              <span>{thread.user?.username ?? "Anonymous"}</span>
                            </div>
                            <span></span>
                            <span>{format(new Date(thread.createdAt), "MMM d, yyyy")}</span>
                            <span></span>
                            <div className="flex items-center gap-1">
                              <MessageSquare className="h-3 w-3" />
                              <span>{thread.commentsCount} comments</span>
                            </div>
                          </div>
                        </div>
                      </div>
                    </Card>
                  ))
                )}
              </div>

              {/* Thread Detail / Comments */}
              <Card className="lg:col-span-1 overflow-hidden flex flex-col max-h-[calc(100vh-16rem)]">
                {!selectedThreadId ? (
                  <div className="flex-1 flex flex-col items-center justify-center p-8 text-center text-muted-foreground">
                    <TrendingUp className="h-12 w-12 mb-4 opacity-50" />
                    <p className="font-medium mb-1">Select a thread</p>
                    <p className="text-sm">Click on a thread to view comments</p>
                  </div>
                ) : (
                  <>
                    <div className="p-5 border-b border-border/50">
                      <h3 className="font-semibold mb-2">{selectedThread?.title}</h3>
                      <p className="text-sm text-muted-foreground">{comments.length} comments</p>
                    </div>

                    <ScrollArea className="flex-1 p-5">
                      {comments.length === 0 ? (
                        <div className="text-center text-muted-foreground py-8">
                          <p className="text-sm">No comments yet. Be the first!</p>
                        </div>
                      ) : (
                        <div className="space-y-4">
                          {comments.map((comment) => (
                            <div key={comment.id} className="space-y-2" data-testid={`comment-${comment.id}`}>
                              <div className="flex items-start gap-2">
                                <Avatar className="h-8 w-8">
                                  <AvatarFallback className="text-xs">
                                    {comment.user?.username?.[0]?.toUpperCase() ?? "U"}
                                  </AvatarFallback>
                                </Avatar>
                                <div className="flex-1 min-w-0">
                                  <div className="flex items-center gap-2 mb-1">
                                    <span className="text-sm font-medium">{comment.user?.username ?? "Anonymous"}</span>
                                    <span className="text-xs text-muted-foreground">
                                      {format(new Date(comment.createdAt), "MMM d")}
                                    </span>
                                  </div>
                                  <p className="text-sm">{comment.content}</p>
                                  <div className="flex items-center gap-2 mt-2">
                                    <Button size="sm" variant="ghost" className="h-6 text-xs gap-1">
                                      <ThumbsUp className="h-3 w-3" />
                                      {comment.upvotes}
                                    </Button>
                                    <Button size="sm" variant="ghost" className="h-6 text-xs gap-1">
                                      <ThumbsDown className="h-3 w-3" />
                                      {comment.downvotes}
                                    </Button>
                                  </div>
                                </div>
                              </div>
                            </div>
                          ))}
                        </div>
                      )}
                    </ScrollArea>

                    <div className="p-5 border-t border-border/50">
                      {!user ? (
                        <div className="text-center text-sm text-muted-foreground py-4">
                          <p>Please log in to post comments</p>
                        </div>
                      ) : (
                        <>
                          <div className="flex gap-2">
                            <Textarea
                              placeholder="Add a comment..."
                              value={newCommentContent}
                              onChange={(e) => setNewCommentContent(e.target.value)}
                              rows={3}
                              data-testid="textarea-comment"
                            />
                          </div>
                          <Button
                            onClick={() => {
                              if (selectedThreadId) {
                                createCommentMutation.mutate({
                                  threadId: selectedThreadId,
                                  content: newCommentContent
                                });
                              }
                            }}
                            disabled={!newCommentContent.trim() || createCommentMutation.isPending}
                            className="w-full mt-2"
                            size="sm"
                            data-testid="button-post-comment"
                          >
                            {createCommentMutation.isPending ? "Posting..." : "Post Comment"}
                          </Button>
                        </>
                      )}
                    </div>
                  </>
                )}
              </Card>
            </div>
          </TabsContent>
        </Tabs>
      </div>
    </div>
  );
}

